import React from "react";
import { useParams } from "react-router-dom";
import { useGetBookingByIdQuery } from "../../redux/api/bookingApi";

const BookingView = () => {
  const { id } = useParams();
  const { data: booking } = useGetBookingByIdQuery(id);

  if (!booking) return <p>Loading...</p>;

  //const paidAmount = booking.paymentHistory.reduce((s, p) => s + p.amount, 0);
  const balance = booking.totalAmount - paidAmount;

  return (
    <div className="container mt-4">
      <h2>Booking Details</h2>
      <p>
        <strong>Name:</strong> {booking.travelerName}
      </p>
      <p>
        <strong>Email:</strong> {booking.email}
      </p>
      <p>
        <strong>Phone:</strong> {booking.mobile}
      </p>
      <p>
        <strong>From:</strong> {booking.from}
      </p>
      <p>
        <strong>To:</strong> {booking.to}
      </p>
      <p>
        <strong>Date:</strong> {booking.travelDate}
      </p>
      <p>
        <strong>Time:</strong> {booking.travelTime}
      </p>
      <p>
        <strong>Status:</strong> {booking.status}
      </p>
      <p>
        <strong>Flight Type:</strong> {booking.flightType}
      </p>
      <p>
        <strong>Total:</strong> ₹{booking.totalAmount}
      </p>
      <p>
        <strong>Paid:</strong> ₹{paidAmount}
      </p>
      <p>
        <strong>Balance:</strong> ₹{balance}
      </p>

      <h4 className="mt-4">Payment History</h4>
      <ul>
        {booking.paymentHistory.map((p, i) => (
          <li key={i}>
            {p.transactionDate?.slice(0, 10)} — ₹{p.amount} via {p.method} (Txn:{" "}
            {p.transactionNo})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookingView;
