// Check price range match
export const matchPriceRange = (price, range) => {
    switch (range) {
        case "0-5L":
            return price <= 500000;

        case "5L-10L":
            return price > 500000 && price <= 1000000;

        case "10L-20L":
            return price > 1000000 && price <= 2000000;

        case "20L+":
            return price > 2000000;

        default:
            return true;
    }
};

// Check capacity range match
export const matchCapacity = (capacity, range) => {
    switch (range) {
        case "1-4":
            return capacity <= 4;

        case "5-8":
            return capacity > 4 && capacity <= 8;

        case "9-18":
            return capacity > 8 && capacity <= 18;

        case "18+":
            return capacity > 18;

        default:
            return true;
    }
};
