import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import {
  useUpdateProfileMutation,
  useGetMeQuery,
} from "../../redux/api/userApi";
import { useLazyLogoutQuery } from "../../redux/api/authApi";
import { toast } from "react-toastify";
import Header from "../Header";
import Footer from "../Footer";
import "../../assets/css/profile.css";

const UserProfile = () => {
  // RTK Query hooks
  const { data: userData, refetch } = useGetMeQuery();
  const [updateProfile, { isLoading: updating }] = useUpdateProfileMutation();
  const [triggerLogout] = useLazyLogoutQuery();

  // Redux state
  const { userInfo } = useSelector((state) => state.auth);

  // Form states
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [avatar, setAvatar] = useState(null);
  const [avatarPreview, setAvatarPreview] = useState("/default-avatar.png");

  // Populate form on load
  useEffect(() => {
    const user = userData?.user || userInfo;
    if (user) {
      setName(user.name || "");
      setEmail(user.email || "");
      setPhone(user.phone || "");
      setAvatarPreview(user.avatar?.url || "/default-avatar.png");
    }
  }, [userData, userInfo]);

  // ✅ Handle avatar change
  const onAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAvatar(file);
      setAvatarPreview(URL.createObjectURL(file));
    }
  };

  // ✅ Handle profile update
  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", name);
      formData.append("email", email);
      formData.append("phone", phone);
      if (avatar) formData.append("avatar", avatar);

      const res = await updateProfile(formData).unwrap();
      if (res.success) {
        toast.success("Profile updated successfully!");
        refetch();
      }
    } catch (err) {
      toast.error(err?.data?.message || "Failed to update profile");
    }
  };

  // ✅ Handle logout
  const handleLogout = async () => {
    try {
      await triggerLogout().unwrap();
      toast.success("Signed out successfully!");
      window.location.href = "/login"; // redirect after logout
    } catch (err) {
      toast.error("Failed to logout");
    }
  };

  return (
    <>
      <Header />
      {/* ✅ Profile Tabs & Form */}
      <div className="container mx-auto px-4 mt-10">
        <div className="bg-white shadow-md rounded-2xl overflow-hidden">
          {/* Tabs */}
          <div className="border-b flex">
            <button className="px-6 py-3 text-blue-600 font-medium border-b-2 border-blue-600">
              Profile Details
            </button>
          </div>

          {/* Form */}
          <form
            onSubmit={handleProfileSubmit}
            className="p-6 grid grid-cols-1 md:grid-cols-2 gap-6"
          >
            {/* Name */}
            <div className="flex flex-col">
              <label className="text-gray-600 font-medium mb-1">
                Full Name
              </label>
              <input
                type="text"
                className="border rounded-xl px-4 py-2 focus:ring focus:outline-none"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Enter your full name"
                required
              />
            </div>

            {/* Email */}
            <div className="flex flex-col">
              <label className="text-gray-600 font-medium mb-1">Email</label>
              <input
                type="email"
                className="border rounded-xl px-4 py-2 bg-gray-100 cursor-not-allowed"
                value={email}
                disabled
              />
            </div>

            {/* Phone */}
            <div className="flex flex-col">
              <label className="text-gray-600 font-medium mb-1">Phone</label>
              <input
                type="text"
                className="border rounded-xl px-4 py-2 focus:ring focus:outline-none"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                placeholder="Enter phone number"
                required
              />
            </div>

            {/* Avatar (hidden field) */}
            <div className="flex flex-col">
              <label className="text-gray-600 font-medium mb-1">Avatar</label>
              <input
                type="file"
                accept="image/*"
                className="border rounded-xl px-4 py-2"
                onChange={onAvatarChange}
              />
            </div>

            {/* Save Button */}
            <div className="md:col-span-2 flex justify-end mt-4">
              <button
                type="submit"
                className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-xl font-medium shadow"
              >
                Save Changes
              </button>
            </div>
          </form>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default UserProfile;
