// src/components/DealsList.js
import React, { useMemo, useState, useEffect } from "react";
import Header from "../Header";
import Footer from "../Footer";
import { useGetEmptyLegsQuery } from "../redux/api/emptyLegApi";
import { useGetAirportsQuery } from "../redux/api/airportApi";

function haversineDistance(lat1, lon1, lat2, lon2) {
    // returns distance in KM
    const toRad = (v) => (v * Math.PI) / 180;
    const R = 6371; // km
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
        Math.sin(dLat / 2) * Math.sin(dLat / 2) +
        Math.cos(toRad(lat1)) *
        Math.cos(toRad(lat2)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
}

const priceRanges = [
    "All",
    "₹ 0 - 1,00,000",
    "₹ 1,00,000 - 2,00,000",
    "₹ 2,00,000 - 3,00,000",
    "₹ 3,00,000+",
];

const capacityRanges = [
    "All",
    "5 Persons",
    "6 Persons",
    "7 Persons",
    "8 Persons",
    "9+ Persons",
];

function matchPriceRange(price, range) {
    switch (range) {
        case "₹ 0 - 1,00,000":
            return price >= 0 && price <= 100000;
        case "₹ 1,00,000 - 2,00,000":
            return price > 100000 && price <= 200000;
        case "₹ 2,00,000 - 3,00,000":
            return price > 200000 && price <= 300000;
        case "₹ 3,00,000+":
            return price > 300000;
        default:
            return true;
    }
}

function matchCapacity(capacity, range) {
    switch (range) {
        case "5 Persons":
            return capacity === 5;
        case "6 Persons":
            return capacity === 6;
        case "7 Persons":
            return capacity === 7;
        case "8 Persons":
            return capacity === 8;
        case "9+ Persons":
            return capacity >= 9;
        default:
            return true;
    }
}

// Format date to "25 April 2023"
function formatDate(d) {
    if (!d) return "";
    const dt = new Date(d);
    try {
        return dt.toLocaleDateString("en-GB", {
            day: "2-digit",
            month: "long",
            year: "numeric",
        });
    } catch {
        return d;
    }
}

// Format time to "03:00 PM"
function formatTime(t) {
    if (!t) return "";
    // assume t is "HH:MM" or "HH:MM:SS" or a time string
    const [hhmm] = t.split(".");
    const parts = hhmm.split(":");
    if (parts.length < 2) return t;
    const date = new Date();
    date.setHours(Number(parts[0]), Number(parts[1]), 0);
    return date.toLocaleTimeString("en-US", {
        hour: "2-digit",
        minute: "2-digit",
    });
}

export default function DealsList() {
    const { data: emptyLegData, isLoading: legsLoading } = useGetEmptyLegsQuery();
    const { data: airportsRaw } = useGetAirportsQuery();

    const emptyLegs = emptyLegData?.emptyLegs || [];
    const airports = airportsRaw?.airports || [];

    const [priceFilters, setPriceFilters] = useState(["All"]);
    const [capacityFilters, setCapacityFilters] = useState(["All"]);

    // Booking modal state
    const [showBooking, setShowBooking] = useState(false);
    const [bookingLeg, setBookingLeg] = useState(null);

    // helper to get airport coords by IATA
    const getAirport = (iata) =>
        airports.find((a) => a.iata_code === iata || a.iata === iata);

    // Enhance legs with computed fields (distance, travelTimeMins)
    const enhancedLegs = useMemo(() => {
        return emptyLegs.map((leg) => {
            const fromAp = getAirport(leg.from);
            const toAp = getAirport(leg.to);
            let distanceKm = 0;
            if (fromAp && toAp && fromAp.latitude_deg && toAp.latitude_deg) {
                distanceKm = haversineDistance(
                    Number(fromAp.latitude_deg),
                    Number(fromAp.longitude_deg),
                    Number(toAp.latitude_deg),
                    Number(toAp.longitude_deg)
                );
            }

            // flightType may exist
            const flight = leg.flightType || {};
            // If speed exists use it, else fallback to 500 km/hr
            const maxSpeed = Number(flight.maxSpeed || flight.max_speed || 500);

            const travelTimeHours = maxSpeed > 0 ? distanceKm / maxSpeed : 0;
            const travelTimeMins = Math.max(1, Math.round(travelTimeHours * 60));

            return {
                ...leg,
                _computed: {
                    distanceKm: Math.round(distanceKm),
                    travelTimeMins,
                },
            };
        });
    }, [emptyLegs, airportsRaw]);

    // Filters handling
    const handleCheckboxChange = (e, category, value) => {
        const checked = e.target.checked;

        const handleFilter = (filters, setFilters) => {
            if (value === "All") return setFilters(["All"]);
            const updated = checked
                ? [...filters.filter((f) => f !== "All"), value]
                : filters.filter((f) => f !== value);
            setFilters(updated.length ? updated : ["All"]);
        };

        if (category === "price") handleFilter(priceFilters, setPriceFilters);
        if (category === "capacity")
            handleFilter(capacityFilters, setCapacityFilters);
    };

    const filteredLegs = useMemo(() => {
        let legs = enhancedLegs || [];

        // price filter uses offerPrice if present else exactPrice
        if (!priceFilters.includes("All")) {
            legs = legs.filter((leg) =>
                priceFilters.some((range) =>
                    matchPriceRange(Number(leg.offerPrice || leg.exactPrice || 0), range)
                )
            );
        }

        if (!capacityFilters.includes("All")) {
            legs = legs.filter((leg) =>
                capacityFilters.some((range) =>
                    matchCapacity(Number(leg.flightType?.capacity || 0), range)
                )
            );
        }

        return legs;
    }, [enhancedLegs, priceFilters, capacityFilters]);

    // Open booking modal
    const openBooking = (leg) => {
        setBookingLeg(leg);
        setShowBooking(true);
    };

    // Close booking modal
    const closeBooking = () => {
        setBookingLeg(null);
        setShowBooking(false);
    };

    // Submit booking (this is a simple client-side demo — replace with your flow)
    const handleBookingSubmit = (e) => {
        e.preventDefault();
        // collect form data
        const fd = new FormData(e.target);
        // send to your booking endpoint or show success
        console.log("Booking payload:", Object.fromEntries(fd.entries()));
        alert("Booking request submitted!");
        closeBooking();
    };

    return (
        <>
            <Header />

            <div className="inner-heading">
                <h1>Empty Leg</h1>
            </div>

            {/* Main content */}
            <div className="listing-container">
                <div className="container-fluid">
                    <div className="listing">
                        {/* Filters */}
                        <div className="filter">
                            <div className="filter-box">
                                <h2>Price Range</h2>
                                <ul>
                                    {priceRanges.map((range) => (
                                        <li key={range}>
                                            <label className="checkbox style-c">
                                                <input
                                                    type="checkbox"
                                                    checked={priceFilters.includes(range)}
                                                    onChange={(e) =>
                                                        handleCheckboxChange(e, "price", range)
                                                    }
                                                />
                                                <div className="checkbox__checkmark"></div>
                                            </label>
                                            {range.replace("All", "All")}
                                        </li>
                                    ))}
                                </ul>
                            </div>

                            <div className="filter-box">
                                <h2>Capacity</h2>
                                <ul>
                                    {capacityRanges.map((cap) => (
                                        <li key={cap}>
                                            <label className="checkbox style-c">
                                                <input
                                                    type="checkbox"
                                                    checked={capacityFilters.includes(cap)}
                                                    onChange={(e) =>
                                                        handleCheckboxChange(e, "capacity", cap)
                                                    }
                                                />
                                                <div className="checkbox__checkmark"></div>
                                            </label>
                                            {cap}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>

                        {/* Listing box */}
                        <div className="listing-box">
                            {filteredLegs.length ? (
                                filteredLegs.map((leg) => {
                                    const flight = leg.flightType || {};
                                    const img =
                                        flight.images?.[0]?.url ||
                                        `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`;
                                    const distanceLabel =
                                        leg._computed?.distanceKm != null
                                            ? `${leg._computed.distanceKm} KM`
                                            : "";
                                    const travelTimeMin = leg._computed?.travelTimeMins || 0;
                                    const travelTimeDisplay = `${Math.floor(
                                        travelTimeMin / 60
                                    )} hr ${travelTimeMin % 60} min`;
                                    const priceDisplay = Number(
                                        leg.offerPrice || leg.exactPrice || 0
                                    ).toLocaleString();

                                    return (
                                        <div key={leg._id} className="listing-row">
                                            <div className="thumb">
                                                <img
                                                    src={img}
                                                    alt={flight.name || "Aircraft"}
                                                    height={200}
                                                    width={200}
                                                />
                                            </div>

                                            <div className="content">
                                                <h2>
                                                    {flight.name || "Aircraft"}{" "}
                                                    <div className="emptyleg-info">
                                                        <div className="icon">
                                                            <img
                                                                src={`${process.env.PUBLIC_URL}/assets/images/icon-airplane-color.png`}
                                                                alt="Airplane"
                                                            />
                                                        </div>{" "}
                                                        <div className="text">
                                                            {leg.from}{" "}
                                                            <div className="sep">
                                                                <img
                                                                    src={`${process.env.PUBLIC_URL}/assets/images/icon-takeoff.png`}
                                                                    alt="take off"
                                                                />
                                                                ...............
                                                            </div>{" "}
                                                            {leg.to}
                                                        </div>
                                                    </div>
                                                </h2>

                                                <p>
                                                    {(flight.description &&
                                                        flight.description.replace(/<[^>]+>/g, "")) ||
                                                        "No description available"}
                                                </p>

                                                <div className="stats">
                                                    <div className="stats-col">
                                                        <div className="icon">
                                                            <img
                                                                src={`${process.env.PUBLIC_URL}/assets/images/icon-speed.png`}
                                                                alt="speed"
                                                            />
                                                        </div>
                                                        <p>
                                                            {flight.maxSpeed || flight.max_speed || "N/A"}{" "}
                                                            km/hr
                                                        </p>
                                                    </div>
                                                    <div className="stats-col">
                                                        <div className="icon">
                                                            <img
                                                                src={`${process.env.PUBLIC_URL}/assets/images/icon-person.png`}
                                                                alt="Person"
                                                            />
                                                        </div>
                                                        <p>{flight.capacity || "N/A"} Person</p>
                                                    </div>
                                                    <div className="stats-col">
                                                        <div className="icon">
                                                            <img
                                                                src={`${process.env.PUBLIC_URL}/assets/images/icon-time.png`}
                                                                alt="Time"
                                                            />
                                                        </div>
                                                        <p>{travelTimeDisplay}</p>
                                                    </div>
                                                    <div className="stats-col">
                                                        <div className="icon">
                                                            <img
                                                                src={`${process.env.PUBLIC_URL}/assets/images/icon-luggage.png`}
                                                                alt="luggage"
                                                            />
                                                        </div>
                                                        <p>Upto {flight.baggage || "N/A"} kg</p>
                                                    </div>
                                                </div>
                                            </div>

                                            {/* Price / action */}
                                            <div className="price">
                                                <div className="label">Price</div>
                                                <div className="amount">
                                                    <span className="material-icons-outlined">
                                                        currency_rupee
                                                    </span>{" "}
                                                    {priceDisplay}
                                                </div>
                                                <div className="button">
                                                    <button
                                                        className="btn btn-fill"
                                                        onClick={() => openBooking(leg)}
                                                    >
                                                        Book Now
                                                    </button>
                                                </div>
                                                <div className="date-info">
                                                    <div className="dt-info">
                                                        <img
                                                            src={`${process.env.PUBLIC_URL}/assets/images/icon-time-small.png`}
                                                            alt="Time"
                                                        />{" "}
                                                        {formatTime(leg.time)}
                                                    </div>
                                                    <div className="dt-info">
                                                        <img
                                                            src={`${process.env.PUBLIC_URL}/assets/images/icon-calendar-small.png`}
                                                            alt="calendar"
                                                        />{" "}
                                                        {formatDate(leg.date)}
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    );
                                })
                            ) : (
                                <div className="nomore-records">
                                    {legsLoading ? "Loading deals..." : "No more records"}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>

            <Footer />

            {/* Booking Modal (inline copy of your HTML popup) */}
            {showBooking && bookingLeg && (
                <div
                    id="bookingform"
                    className="ppform"
                    style={{
                        display: "block",
                        maxWidth: 600,
                        position: "fixed",
                        left: "50%",
                        top: "50%",
                        transform: "translate(-50%, -50%)",
                        zIndex: 999999,
                        background: "#fff",
                        padding: 20,
                        borderRadius: 8,
                    }}
                >
                    <button
                        onClick={closeBooking}
                        style={{
                            position: "absolute",
                            right: 10,
                            top: 10,
                            border: "none",
                            background: "transparent",
                            fontSize: 20,
                            cursor: "pointer",
                        }}
                        aria-label="Close"
                    >
                        ×
                    </button>

                    <h2>Book {bookingLeg.flightType?.name || "Aircraft"}</h2>

                    <form onSubmit={handleBookingSubmit}>
                        <div className="pp-formrow pp-form-row-half">
                            <div>
                                <label htmlFor="whenfly">When do you want to fly?</label>
                                <input
                                    type="date"
                                    name="whenfly"
                                    required
                                    className="icon-date"
                                />
                            </div>
                            <div>
                                <label htmlFor="flytime">At what time?</label>
                                <input type="time" name="flytime" className="icon-time" />
                            </div>
                        </div>

                        <div className="pp-formrow pp-form-row-full">
                            <div>
                                <label htmlFor="name">Name</label>
                                <input type="text" name="name" placeholder="John Doe" required />
                            </div>
                        </div>

                        <div className="pp-formrow pp-form-row-full">
                            <div>
                                <label htmlFor="email">Email</label>
                                <input
                                    type="email"
                                    name="email"
                                    placeholder="john.doe@mail.com"
                                    required
                                />
                            </div>
                        </div>

                        <div className="pp-formrow pp-form-row-full">
                            <div>
                                <label htmlFor="mobile">Phone number</label>
                                <input type="text" name="mobile" placeholder="9876543210" />
                            </div>
                        </div>

                        <div className="pp-formrowbutton" style={{ marginTop: 12 }}>
                            <input type="submit" className="btn-primary" value="Continue" />
                        </div>
                    </form>
                </div>
            )}
        </>
    );
}
