import { useNavigate, useLocation } from "react-router-dom";
import { useState } from "react";

export default function Login() {
  const navigate = useNavigate();
  const location = useLocation();
  const query = new URLSearchParams(location.search);

  const bookingParams = location.search; // preserve all params
  const [isLogin, setIsLogin] = useState(true);

  const handleGuestUser = () => {
    navigate(`/enquiry${bookingParams}`);
  };

  const handleLoginSuccess = () => {
    localStorage.setItem("token", "dummy-jwt"); // replace with real token
    navigate(`/dashboard${bookingParams}`);
  };

  return (
    <div className="auth-page">
      <h2>{isLogin ? "Login" : "Sign Up"}</h2>

      {isLogin ? (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleLoginSuccess();
          }}
        >
          <input type="email" placeholder="Email" required />
          <input type="password" placeholder="Password" required />
          <button type="submit">Login</button>
        </form>
      ) : (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            // Signup API call here
            handleLoginSuccess();
          }}
        >
          <input type="text" placeholder="Name" required />
          <input type="email" placeholder="Email" required />
          <input type="password" placeholder="Password" required />
          <button type="submit">Sign Up</button>
        </form>
      )}

      <div className="auth-options">
        <p onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? "Create Account" : "Already have an account? Login"}
        </p>
        <button onClick={handleGuestUser}>Continue as Guest</button>
      </div>
    </div>
  );
}
