import React, { useState } from "react";
import {
  useGetPriceSettingsQuery,
  useDeletePriceSettingMutation,
} from "../../redux/api/priceSettingApi";
import PriceSettingForm from "./PriceSettingForm";
import toast from "react-hot-toast";

const PriceSettingList = () => {
  const { data: settings, isLoading, refetch } = useGetPriceSettingsQuery();
  const [deletePriceSetting] = useDeletePriceSettingMutation();
  const [showForm, setShowForm] = useState(false);
  const [editingSetting, setEditingSetting] = useState(null);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this setting?")) return;
    try {
      await deletePriceSetting(id).unwrap();
      toast.success("Deleted successfully");
    } catch (err) {
      toast.error("Failed to delete");
    }
  };

  if (isLoading) return <p>Loading...</p>;

  return (
    <div>
      <div className="d-flex justify-content-between mb-3">
        <h4>Price Calculation Settings</h4>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditingSetting(null);
            setShowForm(true);
          }}
        >
          Add Setting
        </button>
      </div>

      {showForm && (
        <PriceSettingForm
          editingSetting={editingSetting}
          setShowForm={setShowForm}
        />
      )}

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Head Name</th>
            <th>Type</th>
            <th>Value</th>
            <th>Depends On</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {settings?.map((s) => (
            <tr key={s._id}>
              <td>{s.headName}</td>
              <td>{s.type}</td>
              <td>{s.type === "fixed" ? s.amount : `${s.percentage}%`}</td>
              <td>
                {s.dependentHeads?.map((h) => h.headName).join(", ") || "-"}
              </td>
              <td>
                <button
                  className="btn btn-sm btn-warning me-2"
                  onClick={() => {
                    setEditingSetting(s);
                    setShowForm(true);
                  }}
                >
                  Edit
                </button>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => handleDelete(s._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PriceSettingList;
