import React, { useState } from "react";
import {
  useDeleteBookingMutation,
  useGetBookingsQuery,
} from "../../redux/api/bookingApi";
import { Link } from "react-router-dom";
import AddBookingForm from "./AddBookingForm";
import toast from "react-hot-toast";

const BookingList = () => {
  const [showForm, setShowForm] = useState(false);
  const [page, setPage] = useState(1);

  const [editBooking, setEditBooking] = useState(null);
  const { data, isLoading, refetch } = useGetBookingsQuery({ page });
  const [deleteBooking] = useDeleteBookingMutation();
  const { bookings, total, pages } = data || {};
  const handlePrev = () => setPage((p) => Math.max(p - 1, 1));
  const handleNext = () => setPage((p) => (p < pages ? p + 1 : p));

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this booking?")) {
      try {
        await deleteBooking(id).unwrap();
        toast.success("Booking deleted");
        refetch();
      } catch (err) {
        toast.error("Failed to delete booking");
      }
    }
  };

  const handleEdit = (booking) => {
    setEditBooking(booking);
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditBooking(null);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Bookings</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditBooking(null); // reset to add mode
            setShowForm(!showForm);
          }}
        >
          {showForm ? "Close Form" : "Add Booking"}
        </button>
      </div>

      {showForm && (
        <AddBookingForm
          setShowForm={handleCloseForm}
          refetch={refetch}
          booking={editBooking} // send selected booking if edit
        />
      )}

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
          <table className="table mt-4">
            <thead>
              <tr>
                <th>ID</th>
                <th>Traveler</th>
                <th>Email</th>
                <th>Mobile</th>
                <th>From–To</th>
                <th>Date/Time</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {bookings?.map((b) => (
                <tr key={b._id}>
                  <td>{b._id.slice(-5)}</td>
                  <td>{b.user?.name}</td>
                  <td>{b.user?.email}</td>
                  <td>{b.user?.mobile}</td>
                  <td>
                    {b.fromPlace} → {b.toPlace}
                  </td>
                  <td>
                    {b.travelDate} {b.travelTime}
                  </td>
                  <td>
                    <span
                      className={`badge bg-${
                        b.status === "confirmed"
                          ? "success"
                          : b.status === "cancelled"
                          ? "danger"
                          : "warning"
                      }`}
                    >
                      {b.status}
                    </span>
                  </td>
                  <td>
                    <Link
                      to={`/admin/bookings/${b._id}`}
                      className="btn btn-sm btn-info"
                    >
                      View
                    </Link>
                    <button
                      className="btn btn-sm btn-warning me-2"
                      onClick={() => handleEdit(b)}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(b._id)}
                      className="btn btn-sm btn-danger me-2"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {/* Pagination */}
          <div className="d-flex justify-content-between align-items-center mt-3">
            <button
              className="btn btn-outline-secondary"
              onClick={handlePrev}
              disabled={page === 1}
            >
              Previous
            </button>
            <span>
              Page {page} of {pages}
            </span>
            <button
              className="btn btn-outline-secondary"
              onClick={handleNext}
              disabled={page === pages}
            >
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default BookingList;
