import { useSelector, useDispatch } from "react-redux";
import { useNavigate, useLocation } from "react-router-dom";
import { logout } from "../../redux/slices/authSlice";

export default function Dashboard() {
  const { user, isAuthenticated } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();

  // Booking params jo login page se aaye the
  const bookingParams = new URLSearchParams(location.search);
  const from = bookingParams.get("from");
  const to = bookingParams.get("to");
  const pax = bookingParams.get("pax");

  const handleLogout = () => {
    dispatch(logout());
    navigate("/login");
  };

  if (!isAuthenticated) {
    return (
      <div className="p-5 text-center">
        <h2>Please login to access dashboard</h2>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Welcome, {user?.name || "User"} ðŸ‘‹</h2>
        <button onClick={handleLogout} className="btn btn-outline-danger">
          Logout
        </button>
      </header>

      <section className="user-info">
        <h3>Your Profile</h3>
        <ul>
          <li>
            <strong>Name:</strong> {user?.name}
          </li>
          <li>
            <strong>Email:</strong> {user?.email}
          </li>
          <li>
            <strong>Phone:</strong> {user?.phone}
          </li>
          <li>
            <strong>Role:</strong> {user?.roleName}
          </li>
        </ul>
      </section>

      <section className="booking-info">
        <h3>Booking Details</h3>
        {from && to && pax ? (
          <ul>
            <li>
              <strong>From:</strong> {from}
            </li>
            <li>
              <strong>To:</strong> {to}
            </li>
            <li>
              <strong>Passengers:</strong> {pax}
            </li>
          </ul>
        ) : (
          <p>No active booking request.</p>
        )}
      </section>
    </div>
  );
}
