import React, { useState } from "react";
import {
  useGetFlightsQuery,
  useDeleteFlightMutation,
  useToggleFlightStatusMutation,
} from "../../redux/api/flightApi";
import FlightForm from "./FlightForm";
import toast from "react-hot-toast";

const FlightList = () => {
  const { data, isLoading } = useGetFlightsQuery();
  const [deleteFlight] = useDeleteFlightMutation();
  const [toggleStatus] = useToggleFlightStatusMutation();
  const [editingFlight, setEditingFlight] = useState(null);

  const handleDelete = async (id) => {
    await deleteFlight(id);
    toast.success("Flight deleted");
  };

  const handleToggle = async (id) => {
    await toggleStatus(id);
  };

  return (
    <div className="container mt-4">
      <h4>Flight List</h4>
      <FlightForm
        editingFlight={editingFlight}
        onSuccess={() => setEditingFlight(null)}
      />
      <table className="table mt-4">
        <thead>
          <tr>
            <th>Flight</th>
            <th>From – To</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data?.flights?.map((f) => (
            <tr key={f._id}>
              <td>{f.flightType.name}</td>
              <td>
                {f.from} – {f.to}
              </td>
              <td>{f.date.split("T")[0]}</td>
              <td>{f.time}</td>
              <td>
                <span
                  className={`badge ${
                    f.status === "active" ? "bg-success" : "bg-secondary"
                  }`}
                >
                  {f.status}
                </span>
              </td>
              <td>
                <button
                  onClick={() => setEditingFlight(f)}
                  className="btn btn-sm btn-warning me-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(f._id)}
                  className="btn btn-sm btn-danger me-2"
                >
                  Delete
                </button>
                <button
                  onClick={() => handleToggle(f._id)}
                  className="btn btn-sm btn-info"
                >
                  Toggle Status
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FlightList;
