import { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  useGetAirportsQuery,
  useSearchFlightsMutation,
} from "../redux/api/airportApi";
import { useDispatch } from "react-redux";
import { setSearchResults } from "../redux/slices/searchSlice";

export default function BookingEngine() {
  const { data: airports = [], isLoading } = useGetAirportsQuery();
  console.log(airports);
  const [searchFlights] = useSearchFlightsMutation();
  const [form, setForm] = useState({ from: "", to: "", date: "" });

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const result = await searchFlights(form).unwrap();
      dispatch(setSearchResults({ results: result, form }));
      navigate("/results"); // redirect to results page
    } catch (err) {
      console.error("Search failed:", err);
    }
  };

  return (
    <div className="booking-engine">
      {/* Tabs */}
      <div className="booking-tabs">
        <button className="active">Fly Now</button>
        <button>Empty Leg</button>
        <button>Regional</button>
        <button>Pilgrimage</button>
      </div>

      {/* Booking Form */}
      <form id="flynow" className="booking-fields" onSubmit={handleSubmit}>
        <div className="booking-form-row booking-form-half">
          <div className="booking-form-col">
            <label>From</label>
            <select
              name="from"
              value={form.from}
              onChange={handleChange}
              required
              disabled={isLoading}
            >
              <option value="">Select Airport</option>
              {airports?.airports?.map((airport) => (
                <option key={airport.id} value={airport.id}>
                  {airport.name} ({airport.code})
                </option>
              ))}
            </select>
          </div>

          <div className="booking-form-col">
            <label>To</label>
            <select
              name="to"
              value={form.to}
              onChange={handleChange}
              required
              disabled={isLoading}
            >
              <option value="">Select Airport</option>
              {airports?.airports?.map((airport) => (
                <option key={airport.id} value={airport.id}>
                  {airport.name} ({airport.code})
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="booking-form-row booking-form-full">
          <div className="booking-form-col">
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="booking-form-button">
          <input type="submit" className="btn btn-fill" value="Search" />
        </div>
      </form>
    </div>
  );
}
