import React, { useState } from "react";
import toast from "react-hot-toast";
import { useUpdateUserDetailsMutation } from "../../redux/api/adminApi";
import { useGetRolesQuery } from "../../redux/api/adminApi"; // fetch roles

const EditUserForm = ({ user, onClose }) => {
  const [formData, setFormData] = useState({
    name: user.name,
    email: user.email,
    role: user.role.name || "", // should be role name
  });
  console.log("form data: ", formData);
  const [updateUser, { isLoading }] = useUpdateUserDetailsMutation();
  const { data: roleData } = useGetRolesQuery(); // fetch available roles

  useEffect(() => {
    if (roleData?.length) {
      const userRole =
        typeof user.role === "string"
          ? roleData.find((r) => r._id === user.role)?.name
          : user.role?.name;

      setFormData({
        name: user.name,
        email: user.email,
        role: userRole || "", // fallback to empty if role not found
      });
    }
  }, [user, roleData]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.role) {
      toast.error("Role is required");
      return;
    }
    try {
      const roleToSend = formData.role || user.role.name;
      await updateUser({
        id: user._id,
        data: { ...formData, role: roleToSend },
      }).unwrap();

      toast.success("User updated successfully");
      onClose();
    } catch (err) {
      toast.error(err?.data?.message || "Failed to update user");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded bg-light">
      <div className="row g-3">
        <div className="col-md-3">
          <input
            className="form-control"
            type="text"
            name="name"
            value={formData.name}
            placeholder="Name"
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
        </div>
        <div className="col-md-3">
          <input
            className="form-control"
            type="email"
            name="email"
            value={formData.email}
            placeholder="Email"
            onChange={(e) =>
              setFormData({ ...formData, email: e.target.value })
            }
            required
          />
        </div>

        <div className="col-md-3">
          <select
            className="form-select"
            name="role"
            value={formData.role}
            onChange={(e) => setFormData({ ...formData, role: e.target.value })}
          >
            <option value="">Select Role</option>
            {roleData?.map((role) => (
              <option key={role._id} value={role.name}>
                {role.name.charAt(0).toUpperCase() + role.name.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="col-md-3 d-flex gap-2">
          <button
            type="submit"
            className="btn btn-success"
            disabled={isLoading}
          >
            Save
          </button>
          <button type="button" className="btn btn-secondary" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </form>
  );
};

export default EditUserForm;
