// LeadAddForm.jsx
import React, { useState } from "react";
import toast from "react-hot-toast";
import { useAddLeadMutation } from "../../redux/api/leadApi";

const LeadAddForm = ({ refetch, onClose }) => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    fromPlace: "",
    toPlace: "",
    travelDate: "",
    travelTime: "",
  });

  const [addLead, { isLoading }] = useAddLeadMutation();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addLead(form).unwrap();
      toast.success("Lead added successfully");
      if (typeof onClose === "function") onClose();
      if (typeof refetch === "function") refetch();
    } catch (err) {
      toast.error(err?.data?.message || "Failed to add lead");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="border p-3 mb-3">
      <h5>Add New Lead</h5>
      <div className="row">
        <div className="col-md-4 mb-2">
          <input
            className="form-control"
            placeholder="Name"
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-4 mb-2">
          <input
            className="form-control"
            placeholder="Email"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-4 mb-2">
          <input
            className="form-control"
            placeholder="Phone"
            name="phone"
            value={form.phone}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-4 mb-2">
          <input
            className="form-control"
            placeholder="From Place"
            name="fromPlace"
            value={form.fromPlace}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-4 mb-2">
          <input
            className="form-control"
            placeholder="To Place"
            name="toPlace"
            value={form.toPlace}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-2 mb-2">
          <input
            className="form-control"
            type="date"
            name="travelDate"
            value={form.travelDate}
            onChange={handleChange}
            required
          />
        </div>
        <div className="col-md-2 mb-2">
          <input
            className="form-control"
            type="time"
            name="travelTime"
            value={form.travelTime}
            onChange={handleChange}
            required
          />
        </div>
      </div>
      <button
        className="btn btn-success me-2"
        disabled={isLoading}
        type="submit"
      >
        Add Lead
      </button>
      <button className="btn btn-secondary" onClick={onClose} type="button">
        Cancel
      </button>
    </form>
  );
};

export default LeadAddForm;
