import React, { useState } from "react";
import {
  useGetFlightsQuery,
  useDeleteFlightMutation,
  useToggleFlightStatusMutation,
} from "../../redux/api/flightApi";
import FlightForm from "./FlightForm";
import toast from "react-hot-toast";
import DataTable from "react-data-table-component";

const FlightList = () => {
  const { data, isLoading, refetch } = useGetFlightsQuery();
  const [deleteFlight] = useDeleteFlightMutation();
  const [toggleStatus] = useToggleFlightStatusMutation();
  const [editingFlight, setEditingFlight] = useState(null);

  const handleDelete = async (id) => {
    try {
      await deleteFlight(id).unwrap();
      toast.success("Flight deleted");
      refetch();
    } catch {
      toast.error("Failed to delete flight");
    }
  };

  const handleToggle = async (id) => {
    try {
      await toggleStatus(id).unwrap();
      toast.success("Flight status toggled");
      refetch();
    } catch {
      toast.error("Failed to toggle status");
    }
  };

  const columns = [
    {
      name: "Flight",
      selector: (row) => row.flightType?.name || "N/A",
      sortable: true,
    },
    {
      name: "From ‚Äì To",
      selector: (row) => `${row.from} ‚Äì ${row.to}`,
      sortable: true,
    },
    {
      name: "Date",
      selector: (row) => row.date?.split("T")[0],
      sortable: true,
    },
    {
      name: "Time",
      selector: (row) => row.time,
      sortable: true,
    },
    {
      name: "Status",
      cell: (row) => (
        <span
          className={`badge ${
            row.status === "active" ? "bg-success" : "bg-secondary"
          }`}
        >
          {row.status}
        </span>
      ),
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            type="button"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Actions
          </button>
          <ul className="dropdown-menu">
            <li>
              <button
                className="dropdown-item"
                onClick={() => setEditingFlight(row)}
              >
                ‚úèÔ∏è Edit
              </button>
            </li>
            <li>
              <button
                className="dropdown-item text-danger"
                onClick={() => handleDelete(row._id)}
              >
                üóëÔ∏è Delete
              </button>
            </li>
            <li>
              <button
                className="dropdown-item text-info"
                onClick={() => handleToggle(row._id)}
              >
                üîÅ Toggle Status
              </button>
            </li>
          </ul>
        </div>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  return (
    <div className="container mt-4">
      <h4>Flight List</h4>

      <FlightForm
        editingFlight={editingFlight}
        onSuccess={() => {
          setEditingFlight(null);
          refetch();
        }}
      />

      <DataTable
        columns={columns}
        data={data?.flights || []}
        progressPending={isLoading}
        pagination
        responsive
        striped
        highlightOnHover
        //persistTableHead
        noDataComponent="No flights found"
        //className="mt-4"
      />
    </div>
  );
};

export default FlightList;
