import { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useSearchFlightsMutation } from "../redux/api/airportApi";
import { useDispatch } from "react-redux";
import { setSearchResults } from "../redux/slices/searchSlice";
import { airports } from "airports-json";

export default function BookingEngine() {
  const [searchFlights] = useSearchFlightsMutation();
  const [form, setForm] = useState({
    from: "",
    to: "",
    date: "",
    time: "",
    pax: 1,
  });
  console.log("Airports data:", airports);
  const [fromSearch, setFromSearch] = useState("");
  const [toSearch, setToSearch] = useState("");
  const [filteredFrom, setFilteredFrom] = useState([]);
  const [filteredTo, setFilteredTo] = useState([]);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const dateRef = useRef(null);
  const timeRef = useRef(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // --- Handle search filtering for 'From' airport ---
  const handleFromSearch = (e) => {
    const value = e.target.value;
    setFromSearch(value);
    if (value.length > 1) {
      const filtered = airports.filter(
        (a) =>
          a.name.toLowerCase().includes(value.toLowerCase()) ||
          a.municipality.toLowerCase().includes(value.toLowerCase()) ||
          a.iata_code?.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredFrom(filtered.slice(0, 10)); // limit results
    } else {
      setFilteredFrom([]);
    }
  };

  // --- Handle search filtering for 'To' airport ---
  const handleToSearch = (e) => {
    const value = e.target.value;
    setToSearch(value);
    if (value.length > 1) {
      const filtered = airports.filter(
        (a) =>
          a.name.toLowerCase().includes(value.toLowerCase()) ||
          a.municipality.toLowerCase().includes(value.toLowerCase()) ||
          a.iata_code?.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredTo(filtered.slice(0, 10));
    } else {
      setFilteredTo([]);
    }
  };

  // --- When user selects an airport ---
  const selectAirport = (type, airport) => {
    setForm({ ...form, [type]: airport.iata_code });
    if (type === "from") {
      setFromSearch(`${airport.name} (${airport.iata_code})`);
      setFilteredFrom([]);
    } else {
      setToSearch(`${airport.name} (${airport.iata_code})`);
      setFilteredTo([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const result = await searchFlights(form).unwrap();
      dispatch(setSearchResults({ results: result, form }));
      navigate("/results");
    } catch (err) {
      console.error("Search failed:", err);
    }
  };

  return (
    <div className="booking-engine">
      {/* Tabs */}
      <div className="booking-tabs">
        <button className="active">Fly Now</button>
        <button>Empty Leg</button>
        <button>Regional</button>
        <button>Pilgrimage</button>
      </div>

      {/* Booking Form */}
      <form id="flynow" className="booking-fields" onSubmit={handleSubmit}>
        {/* From / To */}
        <div className="booking-form-row booking-form-half">
          {/* From Airport */}
          <div className="booking-form-col" style={{ position: "relative" }}>
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-from.png`}
                alt="From"
              />
              From
            </label>
            <input
              type="text"
              value={fromSearch}
              onChange={handleFromSearch}
              placeholder="Enter departure airport"
              required
            />
            {filteredFrom.length > 0 && (
              <ul
                style={{
                  position: "absolute",
                  background: "#fff",
                  border: "1px solid #ccc",
                  listStyle: "none",
                  margin: 0,
                  padding: 0,
                  width: "100%",
                  maxHeight: "200px",
                  overflowY: "auto",
                  zIndex: 10,
                }}
              >
                {filteredFrom.map((airport) => (
                  <li
                    key={airport.iata_code}
                    onClick={() => selectAirport("from", airport)}
                    style={{
                      padding: "8px",
                      cursor: "pointer",
                    }}
                  >
                    {airport.name} ({airport.iata_code}) —{" "}
                    {airport.municipality}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* To Airport */}
          <div className="booking-form-col" style={{ position: "relative" }}>
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-to.png`}
                alt="To"
              />
              To
            </label>
            <input
              type="text"
              value={toSearch}
              onChange={handleToSearch}
              placeholder="Enter destination airport"
              required
            />
            {filteredTo.length > 0 && (
              <ul
                style={{
                  position: "absolute",
                  background: "#fff",
                  border: "1px solid #ccc",
                  listStyle: "none",
                  margin: 0,
                  padding: 0,
                  width: "100%",
                  maxHeight: "200px",
                  overflowY: "auto",
                  zIndex: 10,
                }}
              >
                {filteredTo.map((airport) => (
                  <li
                    key={airport.iata_code}
                    onClick={() => selectAirport("to", airport)}
                    style={{
                      padding: "8px",
                      cursor: "pointer",
                    }}
                  >
                    {airport.name} ({airport.iata_code}) —{" "}
                    {airport.municipality}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        {/* Date / Time */}
        <div className="booking-form-row booking-form-half">
          <div
            className="booking-form-col clickable-field"
            onClick={() => dateRef.current?.showPicker()}
          >
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-date.png`}
                alt="Date"
              />
              Date
            </label>
            <input
              type="date"
              name="date"
              ref={dateRef}
              value={form.date}
              onChange={handleChange}
              min={new Date().toISOString().split("T")[0]}
              required
            />
          </div>
          <div
            className="booking-form-col clickable-field"
            onClick={() => timeRef.current?.showPicker()}
          >
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-time.png`}
                alt="Time"
              />
              Time
            </label>
            <input
              type="time"
              name="time"
              ref={timeRef}
              value={form.time}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        {/* Pax */}
        <div className="booking-form-row booking-form-full">
          <div className="booking-form-col">
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-user.png`}
                alt="Passengers"
              />
              Passengers
            </label>
            <input
              type="number"
              name="pax"
              min="1"
              value={form.pax}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        {/* Submit */}
        <div className="booking-form-button">
          <input type="submit" className="btn btn-fill" value="Search" />
        </div>
      </form>
    </div>
  );
}
