import React, { useState } from "react";
import DataTable from "react-data-table-component";
import {
  useGetLeadsQuery,
  useCloseLeadMutation,
  useAddRemarkMutation,
  useConvertLeadMutation,
} from "../../redux/api/leadApi";
import toast from "react-hot-toast";
import LeadAddForm from "./LeadAddForm"; // Make sure the path is correct

const LeadList = () => {
  const [filters, setFilters] = useState({
    keyword: "",
    status: "",
    fromDate: "",
    toDate: "",
  });
  const { data, refetch } = useGetLeadsQuery(filters);
  const [closeLead] = useCloseLeadMutation();
  const [showForm, setShowForm] = useState(false);

  const handleCloseLead = async (id) => {
    await closeLead(id);
    toast.success("Lead closed");
    refetch();
  };

  const columns = [
    { name: "Lead ID", selector: (row) => row._id },
    { name: "Name", selector: (row) => row.name },
    { name: "Email", selector: (row) => row.email },
    { name: "Phone", selector: (row) => row.phone },
    { name: "From-To", selector: (row) => `${row.fromPlace} - ${row.toPlace}` },
    { name: "Status", selector: (row) => row.status },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            data-bs-toggle="dropdown"
          >
            Actions
          </button>
          <ul className="dropdown-menu">
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleCloseLead(row._id)}
              >
                Close Lead
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => {
                  /* Show remarks modal */
                }}
              >
                View Remarks
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => {
                  /* Show convert modal */
                }}
              >
                Convert to Booking
              </button>
            </li>
          </ul>
        </div>
      ),
    },
  ];

  return (
    <div className="container mt-4">
      <h4>Manage Leads</h4>

      <div className="d-flex justify-content-between align-items-center mb-3">
        <div>{/* Your filter inputs can be placed here */}</div>
        <button
          className="btn btn-primary"
          onClick={() => setShowForm((prev) => !prev)}
        >
          {showForm ? "Close Form" : "Add Lead"}
        </button>
      </div>

      {showForm && (
        <LeadAddForm onClose={() => setShowForm(false)} refetch={refetch} />
      )}

      <DataTable
        columns={columns}
        data={data?.leads || []}
        pagination
        highlightOnHover
        striped
      />
    </div>
  );
};

export default LeadList;
