import React, { useState, useMemo } from "react";
import {
  useGetFlightsQuery,
  useDeleteFlightMutation,
  useToggleFlightStatusMutation,
} from "../../redux/api/flightApi";
import FlightForm from "./FlightForm";
import toast from "react-hot-toast";
import DataTable from "react-data-table-component";

const FlightList = () => {
  const { data, isLoading, error, refetch } = useGetFlightsQuery();
  const [deleteFlight] = useDeleteFlightMutation();
  const [toggleStatus] = useToggleFlightStatusMutation();
  const [editingFlight, setEditingFlight] = useState(null);
  const [search, setSearch] = useState("");

  const handleDelete = async (id) => {
    await deleteFlight(id);
    toast.success("Flight deleted");
    refetch();
  };

  const handleToggle = async (id) => {
    await toggleStatus(id);
    toast.success("Flight status updated");
    refetch();
  };

  const columns = [
    {
      name: "Flight",
      selector: (row) => row.flightType?.name || "N/A",
      sortable: true,
    },
    {
      name: "From – To",
      selector: (row) => `${row.from} – ${row.to}`,
      sortable: true,
    },
    {
      name: "Date",
      selector: (row) => row.date.split("T")[0],
      sortable: true,
    },
    {
      name: "Time",
      selector: (row) => row.time,
      sortable: true,
    },
    {
      name: "Status",
      cell: (row) => (
        <span
          className={`badge ${
            row.status === "active" ? "bg-success" : "bg-secondary"
          }`}
        >
          {row.status}
        </span>
      ),
      sortable: true,
    },
    {
      name: "Actions",
      cell: (row) => (
        <>
          <button
            onClick={() => setEditingFlight(row)}
            className="btn btn-sm btn-warning me-2"
          >
            Edit
          </button>
          <button
            onClick={() => handleDelete(row._id)}
            className="btn btn-sm btn-danger me-2"
          >
            Delete
          </button>
          <button
            onClick={() => handleToggle(row._id)}
            className="btn btn-sm btn-info"
          >
            Toggle Status
          </button>
        </>
      ),
    },
  ];

  const filteredFlights = useMemo(() => {
    if (!search.trim() || !data?.flights) return data?.flights || [];
    const term = search.toLowerCase();
    return data.flights.filter(
      (f) =>
        f.flightType?.name.toLowerCase().includes(term) ||
        f.from.toLowerCase().includes(term) ||
        f.to.toLowerCase().includes(term) ||
        f.status.toLowerCase().includes(term)
    );
  }, [search, data]);

  return (
    <div className="container mt-4">
      <h4>Flight List</h4>

      <FlightForm
        editingFlight={editingFlight}
        onSuccess={() => {
          setEditingFlight(null);
          refetch();
        }}
      />

      <div className="mb-3 mt-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search flights..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      <DataTable
        columns={columns}
        data={filteredFlights}
        progressPending={isLoading}
        pagination
        highlightOnHover
        striped
        persistTableHead
        noDataComponent="No flights found"
      />
    </div>
  );
};

export default FlightList;
