import { useNavigate, useLocation } from "react-router-dom";
import { useState } from "react";
import { useDispatch } from "react-redux";
import {
  useLoginMutation,
  useRegisterMutation,
  useSendOtpMutation,
  useVerifyOtpMutation,
} from "../../redux/api/authApi";
import { loginSuccess } from "../../redux/slices/authSlice";

export default function Login() {
  const navigate = useNavigate();
  const location = useLocation();
  const dispatch = useDispatch();
  const bookingParams = location.search;

  const [isLogin, setIsLogin] = useState(true);
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [otpCode, setOtpCode] = useState("");

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    password: "",
  });

  const [loginUser, { isLoading: loginLoading }] = useLoginMutation();
  const [registerUser, { isLoading: registerLoading }] = useRegisterMutation();
  const [sendOtp, { isLoading: otpLoading }] = useSendOtpMutation();
  const [verifyOtp, { isLoading: verifyLoading }] = useVerifyOtpMutation();

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleGuestUser = () => {
    if (bookingParams) navigate(`/enquiry${bookingParams}`);
    else navigate(`/`);
  };

  // ✅ Handle OTP sending for registration
  // const handleSendOtp = async () => {
  //   if (!formData.email && !formData.phone) {
  //     alert("Please enter your email or phone number first!");
  //     return;
  //   }

  //   try {
  //     const res = await sendOtp({
  //       email: formData.email || undefined,
  //       phone: formData.phone || undefined,
  //     }).unwrap();

  //     alert(res?.message || "OTP sent successfully!");
  //     setOtpSent(true);
  //   } catch (err) {
  //     alert(err?.data?.message || "Failed to send OTP");
  //   }
  // };

  // Unified OTP flow for Login & Registration
  const handleOtpLogin = async (isRegistration = false) => {
    if (!formData.email && !formData.phone) {
      alert("Please enter your email or phone number first!");
      return;
    }

    try {
      const res = await sendOtp({
        email: formData.email || undefined,
        phone: formData.phone || undefined,
      }).unwrap();

      alert(res?.message || "OTP sent successfully!");
      setOtpSent(true);

      // Prepare redirect query params
      const param = formData.email
        ? `email=${encodeURIComponent(formData.email)}`
        : `phone=${encodeURIComponent(formData.phone)}`;

      const extraParams = [];

      // Add booking params if any (used in your booking flow)
      if (bookingParams) {
        extraParams.push(bookingParams.slice(1));
      }

      // Add registration flag if true
      if (isRegistration) {
        extraParams.push("isRegistration=true");
      }

      const queryString =
        extraParams.length > 0 ? `${param}&${extraParams.join("&")}` : param;

      // ✅ Redirect to OTP Verification Page
      navigate(`/otp-verification?${queryString}`);
    } catch (err) {
      alert(err?.data?.message || "Failed to send OTP");
    }
  };

  // ✅ Handle OTP verification for registration
  const handleVerifyOtp = async () => {
    if (!otpCode) {
      alert("Please enter OTP!");
      return;
    }

    try {
      const res = await verifyOtp({
        email: formData.email || undefined,
        phone: formData.phone || undefined,
        otp: otpCode,
        isRegistration: true,
      }).unwrap();

      if (res?.success) {
        alert("OTP verified successfully!");
        setOtpVerified(true);
      } else {
        alert(res?.message || "OTP verification failed!");
      }
    } catch (err) {
      alert(err?.data?.message || "Invalid OTP");
    }
  };

  // ✅ Main Submit for Login or Registration
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isLogin) {
        // LOGIN
        localStorage.removeItem("token");
        const res = await loginUser({
          email: formData.email,
          password: formData.password,
        }).unwrap();
        dispatch(loginSuccess(res));
        setTimeout(() => {
          navigate(bookingParams ? `/enquiry${bookingParams}` : `/`);
        }, 100);
      } else {
        // REGISTER (only after OTP verified)
        if (!otpVerified) {
          alert("Please verify OTP before registering!");
          return;
        }

        const res = await registerUser({
          name: formData.name,
          email: formData.email,
          phone: formData.phone,
          password: formData.password,
          roleName: "User",
        }).unwrap();

        dispatch(loginSuccess(res));
        navigate(bookingParams ? `/enquiry${bookingParams}` : `/`);
      }
    } catch (err) {
      alert(err?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="login-sec-container">
      <div className="login-sec-info">
        <h1>
          Fly Beyond Limits <br /> with Aayo
        </h1>
        <p>
          Travel is the only purchase that enriches you in ways beyond material
          wealth.
        </p>
      </div>

      <div className="login-sec-form">
        <div className="vector-aricraft">
          <img src="/assets/images/aircraft_with_trail.png" alt="Aircraft" />
        </div>
        <div className="logo">
          <a href="/">
            <img src="/assets/images/aayalogo-big.png" alt="Aayo" />
          </a>
        </div>

        <div className="form-flex">
          <div className="form">
            <div className="heading">{isLogin ? "Welcome" : "Register"}</div>
            <div className="subheading">
              {isLogin ? "Login with Email" : "Create your account"}
            </div>

            <form onSubmit={handleSubmit}>
              {!isLogin && (
                <>
                  <div className="input-box">
                    <label>Full Name</label>
                    <div className="input-flex">
                      <div className="icon">
                        <i className="fa fa-user"></i>
                      </div>
                      <input
                        type="text"
                        name="name"
                        placeholder="John Doe"
                        value={formData.name}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>
                </>
              )}

              <div className="input-box">
                <label>Email Id</label>
                <div className="input-flex">
                  <div className="icon">
                    <img src="/assets/images/icon-email.png" alt="email" />
                  </div>
                  <input
                    type="email"
                    name="email"
                    placeholder="john.doe@mail.com"
                    value={formData.email}
                    onChange={handleChange}
                    required={!formData.phone}
                  />
                </div>
              </div>
              <p style={{ textAlign: "center" }}>OR</p>

              <div className="input-box">
                <label>Phone</label>
                <div className="input-flex">
                  <div className="icon">
                    <i className="fa fa-phone"></i>
                  </div>
                  <input
                    type="text"
                    name="phone"
                    placeholder="9876543210"
                    value={formData.phone}
                    onChange={handleChange}
                    required={!formData.email}
                  />
                </div>
              </div>

              {isLogin && (
                <div className="input-box">
                  <label>Password</label>
                  <div className="input-flex">
                    <div className="icon">
                      <img
                        src="/assets/images/icon-password.png"
                        alt="password"
                      />
                    </div>
                    <input
                      type="password"
                      name="password"
                      placeholder="************"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              )}

              {!isLogin && (
                <>
                  <div className="input-box">
                    <label>Password</label>
                    <div className="input-flex">
                      <div className="icon">
                        <img
                          src="/assets/images/icon-password.png"
                          alt="password"
                        />
                      </div>
                      <input
                        type="password"
                        name="password"
                        placeholder="************"
                        value={formData.password}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>

                  {!otpSent && (
                    <button
                      type="button"
                      className="btn btn-outline"
                      onClick={handleSendOtp}
                      disabled={otpLoading}
                    >
                      {otpLoading ? "Sending OTP..." : "Send OTP"}
                    </button>
                  )}

                  {otpSent && !otpVerified && (
                    <div className="input-box">
                      <label>Enter OTP</label>
                      <div className="input-flex">
                        <input
                          type="text"
                          placeholder="Enter 4-digit OTP"
                          value={otpCode}
                          onChange={(e) => setOtpCode(e.target.value)}
                          required
                        />
                        <button
                          type="button"
                          className="btn btn-fill"
                          onClick={handleVerifyOtp}
                          disabled={verifyLoading}
                        >
                          {verifyLoading ? "Verifying..." : "Verify OTP"}
                        </button>
                      </div>
                    </div>
                  )}
                </>
              )}

              <div className="form-button">
                {/* Normal Login / Register button */}
                <input
                  type="submit"
                  className="btn btn-fill"
                  value={
                    isLogin
                      ? loginLoading
                        ? "Logging in..."
                        : "Login"
                      : registerLoading
                      ? "Registering..."
                      : "Register"
                  }
                />

                {/* OTP-based Login or Registration button */}
                {isLogin ? (
                  <input
                    type="button"
                    className="btn btn-outline"
                    onClick={() => handleOtpLogin(false)} // login flow
                    value={otpLoading ? "Sending OTP..." : "Login with OTP"}
                  />
                ) : (
                  <input
                    type="button"
                    className="btn btn-outline"
                    onClick={() => handleOtpLogin(true)} // registration flow
                    value={otpLoading ? "Sending OTP..." : "Register with OTP"}
                  />
                )}
              </div>
            </form>

            <div className="seperator">or</div>

            <div className="form-bottom-link">
              <p>
                {isLogin
                  ? "Don’t have an account?"
                  : "Already have an account?"}{" "}
                <span
                  style={{ cursor: "pointer", color: "#007bff" }}
                  onClick={() => {
                    setIsLogin(!isLogin);
                    setOtpSent(false);
                    setOtpVerified(false);
                    setOtpCode("");
                  }}
                >
                  {isLogin ? "Register Now" : "Login"}
                </span>
              </p>
              <div className="form-button">
                <input
                  type="button"
                  className="btn btn-guest"
                  onClick={handleGuestUser}
                  value="Continue as Guest"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
