import { useNavigate, useLocation } from "react-router-dom";
import { useState } from "react";
import Header from "../Header";
import Footer from "../Footer";
//import "../css/login.css"; // apne CSS ka path sahi set karna

export default function Login() {
  const navigate = useNavigate();
  const location = useLocation();
  const bookingParams = location.search; // preserve booking params

  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleGuestUser = () => {
    navigate(`/enquiry${bookingParams}`);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isLogin) {
      // ðŸ”‘ Call login API
      console.log("Login with:", formData);
      localStorage.setItem("token", "dummy-jwt"); // replace with real API response
      navigate(`/dashboard${bookingParams}`);
    } else {
      // ðŸ”‘ Call signup API
      console.log("Register with:", formData);
      // after signup, auto-login or redirect to login
      setIsLogin(true);
    }
  };

  return (
    <>
      <Header />
      <div className="login-sec-container">
        {/* Left side */}
        <div className="login-sec-info">
          <h1>
            Fly Beyond Limits <br /> with Aayo
          </h1>
          <p>
            Travel is the only purchase that enriches you in ways beyond
            material wealth.
          </p>
        </div>

        {/* Right side */}
        <div className="login-sec-form">
          <div className="vector-aricraft">
            <img src="/assets/images/aircraft_with_trail.png" alt="Aircraft" />
          </div>
          <div className="logo">
            <a href="/">
              <img src="/assets/images/aayalogo-big.png" alt="Aayo" />
            </a>
          </div>

          <div className="form-flex">
            <div className="form">
              <div className="heading">{isLogin ? "Welcome" : "Register"}</div>
              <div className="subheading">
                {isLogin ? "Login with Email" : "Create your account"}
              </div>

              <form onSubmit={handleSubmit}>
                {!isLogin && (
                  <div className="input-box">
                    <label htmlFor="name">Full Name</label>
                    <div className="input-flex">
                      <div className="icon">
                        <i className="fa fa-user"></i>
                      </div>
                      <input
                        type="text"
                        name="name"
                        placeholder="John Doe"
                        value={formData.name}
                        onChange={handleChange}
                        required
                      />
                    </div>
                  </div>
                )}

                <div className="input-box">
                  <label htmlFor="email">Email Id</label>
                  <div className="input-flex">
                    <div className="icon">
                      <img src="/assets/images/icon-email.png" alt="email" />
                    </div>
                    <input
                      type="email"
                      name="email"
                      placeholder="john.doe@mail.com"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>

                <div className="input-box">
                  <label htmlFor="password">Password</label>
                  <div className="input-flex">
                    <div className="icon">
                      <img
                        src="/assets/images/icon-password.png"
                        alt="password"
                      />
                    </div>
                    <input
                      type="password"
                      name="password"
                      placeholder="************"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>

                {isLogin && (
                  <div className="form-link text-right">
                    <a href="#">Forgot your password?</a>
                  </div>
                )}

                <div className="form-button">
                  <input
                    type="submit"
                    className="btn btn-fill"
                    value={isLogin ? "Login" : "Register"}
                  />
                  {isLogin && (
                    <input
                      type="button"
                      className="btn btn-outline"
                      onClick={() => alert("Login with OTP flow")}
                      value="Login with OTP"
                    />
                  )}
                </div>
              </form>

              <div className="seperator">or</div>

              <div className="form-bottom-link">
                <p>
                  {isLogin ? "Donâ€™t have account?" : "Already have account?"}{" "}
                  <span
                    style={{ cursor: "pointer", color: "#007bff" }}
                    onClick={() => setIsLogin(!isLogin)}
                  >
                    {isLogin ? "Register Now" : "Login"}
                  </span>
                </p>
                <button
                  type="button"
                  className="btn btn-guest"
                  onClick={() => setIsLogin(!isLogin)}
                >
                  {isLogin ? "Register Now" : "Login"}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}
