import { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  useGetAirportsQuery,
  useSearchFlightsMutation,
} from "../redux/api/airportApi";
import { useDispatch } from "react-redux";
import { setSearchResults } from "../redux/slices/searchSlice";

export default function BookingEngine() {
  const [searchFlights] = useSearchFlightsMutation();
  const { data: airportsRaw } = useGetAirportsQuery();

  const airports = airportsRaw?.airports || [];

  const [form, setForm] = useState({
    from: "",
    to: "",
    date: "",
    time: "",
    returnDate: "",
    returnTime: "",
    pax: 1,
  });

  // Main tab: flynow | deals
  const [mainTab, setMainTab] = useState("flynow");

  // Trip Type: oneway | round | deals
  const [tripType, setTripType] = useState("oneway");

  const [fromSearch, setFromSearch] = useState("");
  const [toSearch, setToSearch] = useState("");
  const [filteredFrom, setFilteredFrom] = useState([]);
  const [filteredTo, setFilteredTo] = useState([]);

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const dateRef = useRef(null);
  const timeRef = useRef(null);
  const returnDateRef = useRef(null);
  const returnTimeRef = useRef(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Filter airports
  const handleAirportSearch = (e, type) => {
    const value = e.target.value;
    if (type === "from") setFromSearch(value);
    else setToSearch(value);

    if (value.length > 1) {
      let filtered = airports.filter(
        (a) =>
          a.name.toLowerCase().includes(value.toLowerCase()) ||
          a.municipality.toLowerCase().includes(value.toLowerCase()) ||
          a.iata_code?.toLowerCase().includes(value.toLowerCase())
      );

      if (type === "from") {
        filtered = filtered.filter((a) => a.iata_code !== form.to);
        setFilteredFrom(filtered.slice(0, 10));
      } else {
        filtered = filtered.filter((a) => a.iata_code !== form.from);
        setFilteredTo(filtered.slice(0, 10));
      }
    } else {
      if (type === "from") setFilteredFrom([]);
      else setFilteredTo([]);
    }
  };

  const selectAirport = (type, airport) => {
    if (type === "from" && airport.iata_code === form.to) {
      alert("From and To airports cannot be the same.");
      return;
    }
    if (type === "to" && airport.iata_code === form.from) {
      alert("From and To airports cannot be the same.");
      return;
    }

    setForm({ ...form, [type]: airport.iata_code });

    if (type === "from") {
      setFromSearch(`${airport.name} (${airport.iata_code})`);
      setFilteredFrom([]);
    } else {
      setToSearch(`${airport.name} (${airport.iata_code})`);
      setFilteredTo([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.from === form.to) {
      alert("Departure and Destination airport cannot be the same.");
      return;
    }

    // Deals & offers only redirects
    if (mainTab === "deals") {
      navigate("/deals-search", { state: { ...form } });
      return;
    }

    try {
      const payload = { ...form, tripType };
      const result = await searchFlights(payload).unwrap();

      if (!result.success) {
        alert(result.message || "No results found");
        return;
      }

      dispatch(setSearchResults({ results: result, form: payload }));
      navigate("/results");
    } catch (err) {
      alert(err?.data?.message || "No flights found");
      console.error("Search failed:", err);
    }
  };

  return (
    <div className="booking-engine">
      {/* Main Tabs */}
      <div className="booking-tabs">
        <button
          className={mainTab === "flynow" ? "active" : ""}
          onClick={() => {
            setMainTab("flynow");
            setTripType("oneway"); // default when returning to fly now
          }}
          type="button"
        >
          Fly Now
        </button>

        <button
          className={mainTab === "deals" ? "active deals-btn" : "deals-btn"}
          onClick={() => {
            setMainTab("deals");
            setTripType("deals");
          }}
          type="button"
        >
          Deals & Offers
        </button>
      </div>

      {/* Fly Now → Show Trip Type Radio Buttons */}
      {mainTab === "flynow" && (
        <div className="trip-type-radios">
          <label className="radio-option">
            <input
              type="radio"
              name="tripType"
              value="oneway"
              checked={tripType === "oneway"}
              onChange={() => setTripType("oneway")}
            />
            One Way
          </label>

          <label className="radio-option">
            <input
              type="radio"
              name="tripType"
              value="round"
              checked={tripType === "round"}
              onChange={() => setTripType("round")}
            />
            Round Trip
          </label>
        </div>
      )}

      {/* Booking Form */}
      <form id="flynow" className="booking-fields" onSubmit={handleSubmit}>
        {/* FROM / TO */}
        <div className="booking-form-row booking-form-half">
          {/* From */}
          <div className="booking-form-col" style={{ position: "relative" }}>
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-from.png`}
                alt="From"
              />
              From
            </label>
            <input
              type="text"
              value={fromSearch}
              onChange={(e) => handleAirportSearch(e, "from")}
              placeholder="Enter departure airport"
              required
            />
            {filteredFrom.length > 0 && (
              <ul className="dropdown-list">
                {filteredFrom.map((airport) => (
                  <li
                    key={airport.iata_code}
                    onClick={() => selectAirport("from", airport)}
                  >
                    {airport.name} ({airport.iata_code}) —{" "}
                    {airport.municipality}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* To */}
          <div className="booking-form-col" style={{ position: "relative" }}>
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-to.png`}
                alt="To"
              />
              To
            </label>
            <input
              type="text"
              value={toSearch}
              onChange={(e) => handleAirportSearch(e, "to")}
              placeholder="Enter destination airport"
              required
            />
            {filteredTo.length > 0 && (
              <ul className="dropdown-list">
                {filteredTo.map((airport) => (
                  <li
                    key={airport.iata_code}
                    onClick={() => selectAirport("to", airport)}
                  >
                    {airport.name} ({airport.iata_code}) —{" "}
                    {airport.municipality}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        {/* Departure Date / Time */}
        <div className="booking-form-row booking-form-half">
          <div
            className="booking-form-col clickable-field"
            onClick={() => dateRef.current?.showPicker()}
          >
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-date.png`}
                alt="Date"
              />
              Departure Date
            </label>
            <input
              type="date"
              name="date"
              ref={dateRef}
              value={form.date}
              onChange={handleChange}
              min={new Date().toISOString().split("T")[0]}
              required
            />
          </div>

          <div
            className="booking-form-col clickable-field"
            onClick={() => timeRef.current?.showPicker()}
          >
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-time.png`}
                alt="Time"
              />
              Departure Time
            </label>
            <input
              type="time"
              name="time"
              ref={timeRef}
              value={form.time}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        {/* Return Date / Time (Only for Round Trip) */}
        {mainTab === "flynow" && tripType === "round" && (
          <div className="booking-form-row booking-form-half">
            <div
              className="booking-form-col clickable-field"
              onClick={() => returnDateRef.current?.showPicker()}
            >
              <label>
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/icon-date.png`}
                  alt="Return Date"
                />
                Return Date
              </label>
              <input
                type="date"
                name="returnDate"
                ref={returnDateRef}
                value={form.returnDate}
                onChange={handleChange}
                min={form.date || new Date().toISOString().split("T")[0]}
                required
              />
            </div>

            <div
              className="booking-form-col clickable-field"
              onClick={() => returnTimeRef.current?.showPicker()}
            >
              <label>
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/icon-time.png`}
                  alt="Return Time"
                />
                Return Time
              </label>
              <input
                type="time"
                name="returnTime"
                ref={returnTimeRef}
                value={form.returnTime}
                onChange={handleChange}
                required
              />
            </div>
          </div>
        )}

        {/* Pax */}
        <div className="booking-form-row booking-form-full">
          <div className="booking-form-col">
            <label>
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-user.png`}
                alt="Passengers"
              />
              Passengers
            </label>
            <input
              type="number"
              name="pax"
              min="1"
              value={form.pax}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        {/* Submit */}
        <div className="booking-form-button">
          <input type="submit" className="btn btn-fill" value="Search" />
        </div>
      </form>
    </div>
  );
}
