import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { useState, useMemo } from "react";

export default function Results() {
  // default values to avoid undefined errors
  const { results = {}, form = {} } = useSelector(
    (state) => state.search || {}
  );

  // local states for filters
  const [priceFilters, setPriceFilters] = useState(["All"]);
  const [capacityFilters, setCapacityFilters] = useState(["All"]);

  // ----------------------
  // Helper functions — MUST be defined BEFORE useMemo
  // ----------------------
  function matchPriceRange(price, range) {
    switch (range) {
      case "₹ 0 - 1,00,000":
        return price >= 0 && price <= 100000;
      case "₹ 1,00,000 - 2,00,000":
        return price > 100000 && price <= 200000;
      case "₹ 2,00,000 - 3,00,000":
        return price > 200000 && price <= 300000;
      case "₹ 3,00,000+":
        return price > 300000;
      default:
        return true;
    }
  }

  function matchCapacity(capacity, range) {
    switch (range) {
      case "5 Persons":
        return capacity === 5;
      case "6 Persons":
        return capacity === 6;
      case "7 Persons":
        return capacity === 7;
      case "8 Persons":
        return capacity === 8;
      case "9+ Persons":
        return capacity >= 9;
      default:
        return true;
    }
  }

  // ----------------------
  // Checkbox handler
  // ----------------------
  const handleCheckboxChange = (e, category, value) => {
    const checked = e.target.checked;

    if (category === "price") {
      if (value === "All") {
        setPriceFilters(["All"]);
      } else {
        const updated = checked
          ? [...priceFilters.filter((f) => f !== "All"), value]
          : priceFilters.filter((f) => f !== value);
        setPriceFilters(updated.length ? updated : ["All"]);
      }
    }

    if (category === "capacity") {
      if (value === "All") {
        setCapacityFilters(["All"]);
      } else {
        const updated = checked
          ? [...capacityFilters.filter((f) => f !== "All"), value]
          : capacityFilters.filter((f) => f !== value);
        setCapacityFilters(updated.length ? updated : ["All"]);
      }
    }
  };

  // ----------------------
  // Derived filtered flights
  // ----------------------
  // const filteredFlights = useMemo(() => {
  //   let flights = results?.flights || [];

  //   console.log("Filtered Flights:", flights);

  //   // PRICE FILTER
  //   if (!priceFilters.includes("All")) {
  //     flights = flights.filter((flight) => {
  //       const totalPrice = (flight.priceHeads || []).reduce(
  //         (sum, head) => sum + (Number(head.amount) || 0),
  //         0
  //       );
  //       return priceFilters.some((range) => matchPriceRange(totalPrice, range));
  //     });
  //   }

  //   // CAPACITY FILTER
  //   if (!capacityFilters.includes("All")) {
  //     flights = flights.filter((flight) =>
  //       capacityFilters.some((range) =>
  //         matchCapacity(Number(flight.capacity || 0), range)
  //       )
  //     );
  //   }

  //   return flights;
  // }, [results, priceFilters, capacityFilters]);

  const filteredFlights = useMemo(() => {
    let flights = results?.flights || [];

    console.log("Filtered Flights:", flights);

    // Helper to calculate grand total for each flight
    const calculateTotalPrice = (flight) => {
      // Find the Flying Cost head
      const flyingCostHead = flight.priceHeads?.find(
        (heads) => heads.head === "68a2c4d345a4c290c0959fe5"
      );

      const otherHeads =
        flight.priceHeads?.filter(
          (heads) => heads.head !== "68a2c4d345a4c290c0959fe5"
        ) || [];

      // Convert travel time (minutes → hours)
      const travelHours = (Number(flight.travelTime + 30) || 0) / 60;

      // Calculate
      const flyingCostPerHour = Number(flyingCostHead?.amount) || 0;
      const flyingCostTotal = flyingCostPerHour * travelHours;

      const otherCostTotal = otherHeads.reduce(
        (sum, head) => sum + (Number(head.amount) || 0),
        0
      );

      const basePrice = flyingCostTotal + otherCostTotal;
      const taxesAndFees = Math.round(basePrice * 0.12);

      // Final grand total (use same as display)
      const grandTotal = flyingCostTotal;

      return grandTotal;
    };

    // PRICE FILTER
    if (!priceFilters.includes("All")) {
      flights = flights.filter((flight) => {
        const totalPrice = calculateTotalPrice(flight);
        return priceFilters.some((range) => matchPriceRange(totalPrice, range));
      });
    }

    // CAPACITY FILTER
    if (!capacityFilters.includes("All")) {
      flights = flights.filter((flight) =>
        capacityFilters.some((range) =>
          matchCapacity(Number(flight.capacity || 0), range)
        )
      );
    }

    return flights;
  }, [results, priceFilters, capacityFilters]);

  console.log("results", results);

  const calculateTotalPrice = (flight) => {
    if (!flight || !flight.basePrice) return 0;

    // You can adjust this multiplier as needed (e.g., fuel, taxes, etc.)
    const repositionCost =
      flight.baseDistanceFromFromAirport &&
      flight.baseDistanceFromFromAirport > 0
        ? flight.baseDistanceFromFromAirport * (flight.pricePerKm || 0)
        : 0;

    const total = flight.basePrice + repositionCost;

    // Round to two decimal places
    return Math.round(total * 100) / 100;
  };

  const renderFlightCard = (flight, from, to, date, time, pax) => (
    <>
      <div className="thumb">
        <img
          src={
            flight.images?.[0]?.url ||
            `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
          }
          alt={flight.name}
        />
      </div>
      <div className="content">
        <h2>{flight.name}</h2>
        <p>
          {flight.description?.replace(/<[^>]+>/g, "") ||
            "No description available"}
        </p>
        <div className="stats">
          <div className="stats-col">
            <div className="icon">
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-speed.png`}
                alt="speed"
              />
            </div>
            <p>{flight.maxSpeed || "N/A"} km/hr</p>
          </div>
          <div className="stats-col">
            <div className="icon">
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-person.png`}
                alt="capacity"
              />
            </div>
            <p>{flight.capacity} Person</p>
          </div>
          <div className="stats-col">
            <div className="icon">
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-time.png`}
                alt="time"
              />
            </div>
            <p>
              {Math.floor((flight.travelTime + 30) / 60)} hr{" "}
              {(flight.travelTime + 30) % 60} min
            </p>
          </div>
          <div className="stats-col">
            <div className="icon">
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/icon-luggage.png`}
                alt="luggage"
              />
            </div>
            <p>Upto {flight.baggage || "N/A"} kg</p>
          </div>
        </div>
      </div>

      <div className="price">
        <div className="label">Price</div>
        <div className="amount">
          ₹{" "}
          {calculateTotalPrice(flight).toLocaleString(undefined, {
            minimumFractionDigits: 2,
            maximumFractionDigits: 2,
          })}
        </div>
        <div className="button">
          <Link
            to={`/flights/${flight._id}?from=${from}&to=${to}&pax=${pax}&date=${date}&time=${time}`}
          >
            View Details
          </Link>
        </div>
      </div>
    </>
  );

  // ----------------------
  // UI
  // ----------------------
  return (
    <>
      {/* Header */}
      <div className="header-container">
        <div className="container-fluid">
          <div className="header">
            <div className="logo">
              <Link to="/">
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/aayologo.png`}
                  alt="Aayo"
                />
              </Link>
            </div>
            <div className="nav">
              <ul>
                <li>
                  <Link className="active" to="/">
                    home
                  </Link>
                </li>
                <li>
                  <Link to="/features">features</Link>
                </li>
                <li>
                  <Link to="/about">about us</Link>
                </li>
                <li>
                  <Link to="/contact">contact us</Link>
                </li>
              </ul>
            </div>
            <div className="button">
              <Link to="/login" className="btn btn-blank">
                Sign in
              </Link>
              <Link to="/register" className="btn btn-fill">
                Sign up
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Heading */}
      <div className="inner-heading">
        <h1>Available Flights</h1>
      </div>

      {/* From / To summary */}
      <div className="match-notfound">
        <div className="location-box available-flight">
          <div className="both-location">
            <div className="both-location-left">
              <div className="both-location-heading">
                {results.fromAirport?.iata_code || "FROM"}
              </div>
              <div className="both-location-fullname">
                ({results.fromAirport?.code || "Origin"})
              </div>
            </div>
            <div className="both-location-middle">
              <div className="icon">
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/icon-airplane-color.png`}
                  alt="airplane"
                />
              </div>
            </div>
            <div className="both-location-right">
              <div className="both-location-heading">
                {results.toAirport?.iata_code || "TO"}
              </div>
              <div className="both-location-fullname">
                ({results.toAirport?.code || "Destination"})
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Flights Listing */}
      <div className="listing-container">
        <div className="container-fluid">
          <div className="listing">
            {/* Filters */}
            <div className="filter">
              <div className="filter-box">
                <h2>Price Range</h2>
                <ul>
                  {[
                    "All",
                    "₹ 0 - 1,00,000",
                    "₹ 1,00,000 - 2,00,000",
                    "₹ 2,00,000 - 3,00,000",
                    "₹ 3,00,000+",
                  ].map((range) => (
                    <li key={range}>
                      <label className="checkbox style-c">
                        <input
                          type="checkbox"
                          checked={priceFilters.includes(range)}
                          onChange={(e) =>
                            handleCheckboxChange(e, "price", range)
                          }
                        />
                        <div className="checkbox__checkmark"></div>
                      </label>{" "}
                      {range}
                    </li>
                  ))}
                </ul>
              </div>

              <div className="filter-box">
                <h2>Capacity</h2>
                <ul>
                  {[
                    "All",
                    "5 Persons",
                    "6 Persons",
                    "7 Persons",
                    "8 Persons",
                    "9+ Persons",
                  ].map((cap) => (
                    <li key={cap}>
                      <label className="checkbox style-c">
                        <input
                          type="checkbox"
                          checked={capacityFilters.includes(cap)}
                          onChange={(e) =>
                            handleCheckboxChange(e, "capacity", cap)
                          }
                        />
                        <div className="checkbox__checkmark"></div>
                      </label>{" "}
                      {cap}
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            {/* Filtered flight list */}
            <div className="listing-right">
              {/* Outbound Flights */}
              <div className="inner-heading">
                <h2>
                  Outbound Flights ({form.from} → {form.to})
                </h2>
              </div>
              <div className="listing-box">
                {filteredFlights && filteredFlights.length > 0 ? (
                  filteredFlights.map((flight) => (
                    <div className="listing-row" key={flight._id}>
                      {renderFlightCard(
                        flight,
                        form.from,
                        form.to,
                        form.date,
                        form.time,
                        form.pax
                      )}
                    </div>
                  ))
                ) : (
                  <div className="nomore-records">
                    No outbound flights found
                  </div>
                )}
              </div>

              {/* Return Flights (only if round trip) */}
              {form.tripType === "round" &&
                results.returnFlights &&
                results.returnFlights.length > 0 && (
                  <>
                    <div className="inner-heading">
                      <h2>
