import React, { useState, useEffect } from "react";
import DataTable from "react-data-table-component";
import { useSearchParams, Link } from "react-router-dom";
import {
  useGetBookingsQuery,
  useDeleteBookingMutation,
} from "../../redux/api/bookingApi";
import toast from "react-hot-toast";
import AddBookingForm from "./AddBookingForm";
import AddPaymentForm from "./AddPaymentForm";

const BookingList = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [searchTerm, setSearchTerm] = useState(
    searchParams.get("search") || ""
  );
  const [page, setPage] = useState(Number(searchParams.get("page")) || 1);
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");

  const [showForm, setShowForm] = useState(false);
  const [editBooking, setEditBooking] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [showPayment, setShowPayment] = useState(false);

  const { data, isLoading, refetch } = useGetBookingsQuery({
    page,
    search: searchTerm || undefined,
    sort: sortField
      ? sortOrder === "desc"
        ? `-${sortField}`
        : sortField
      : undefined,
  });

  const bookings = data?.bookings || [];
  const totalRows = data?.filteredBookingsCount || 0;
  const resPerPage = data?.resPerPage || 10;

  const [deleteBooking] = useDeleteBookingMutation();

  useEffect(() => {
    const query = {};
    if (searchTerm) query.search = searchTerm;
    if (page) query.page = page;
    if (sortField)
      query.sort = sortOrder === "desc" ? `-${sortField}` : sortField;
    setSearchParams(query);
  }, [searchTerm, page, sortField, sortOrder]);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this booking?")) {
      try {
        await deleteBooking(id).unwrap();
        toast.success("Booking deleted");
        refetch();
      } catch (err) {
        toast.error("Failed to delete booking");
      }
    }
  };

  const handleEdit = (booking) => {
    setEditBooking(booking);
    setShowForm(true);
    setShowPayment(false);
  };

  const handlePaymentClick = (booking) => {
    setSelectedBooking(booking);
    setShowForm(false);
    setShowPayment(true);
    setTimeout(() => {
      const paymentForm = document.getElementById("payment-form");
      if (paymentForm) {
        paymentForm.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  const handleSort = (column, direction) => {
    setSortField(column.sortField || column.selector);
    setSortOrder(direction);
    setPage(1);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setPage(1);
    refetch();
  };

  const columns = [
    {
      name: "ID",
      selector: (row) => row._id.slice(-5),
      sortable: true,
      sortField: "_id",
    },
    {
      name: "Traveler",
      selector: (row) => row.user?.name,
      sortable: true,
      sortField: "user.name",
    },
    {
      name: "Email",
      selector: (row) => row.user?.email,
      sortable: true,
      sortField: "user.email",
    },
    {
      name: "Mobile",
      selector: (row) => row.user?.mobile,
      sortable: true,
      sortField: "user.mobile",
    },
    {
      name: "From–To",
      cell: (row) => `${row.fromPlace} → ${row.toPlace}`,
    },
    {
      name: "Date/Time",
      cell: (row) => `${row.travelDate} ${row.travelTime}`,
    },
    {
      name: "Status",
      cell: (row) => (
        <span
          className={`badge bg-${
            row.status === "confirmed"
              ? "success"
              : row.status === "cancelled"
              ? "danger"
              : "warning"
          }`}
        >
          {row.status}
        </span>
      ),
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="btn-group">
          <Link
            to={`/admin/bookings/${row._id}`}
            className="btn btn-sm btn-outline-primary"
          >
            View
          </Link>
          <button
            className="btn btn-sm btn-outline-warning"
            onClick={() => handleEdit(row)}
          >
            Edit
          </button>
          <button
            className="btn btn-sm btn-outline-danger"
            onClick={() => handleDelete(row._id)}
          >
            Delete
          </button>
          <button
            className="btn btn-sm btn-outline-success"
            onClick={() => handlePaymentClick(row)}
          >
            Add Payment
          </button>
        </div>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Booking List</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditBooking(null);
            setShowForm(!showForm);
            setShowPayment(false);
          }}
        >
          {showForm ? "Close Form" : "Add Booking"}
        </button>
      </div>

      {showForm && (
        <AddBookingForm
          setShowForm={() => setShowForm(false)}
          booking={editBooking}
          refetch={refetch}
        />
      )}

      {showPayment && selectedBooking && (
        <AddPaymentForm
          booking={selectedBooking}
          refetch={refetch}
          onClose={() => setShowPayment(false)}
        />
      )}

      <form onSubmit={handleSearch} className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search bookings..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </form>

      <DataTable
        columns={columns}
        data={bookings}
        progressPending={isLoading}
        pagination
        paginationServer
        paginationTotalRows={totalRows}
        paginationPerPage={resPerPage}
        onChangePage={(page) => setPage(page)}
        onSort={handleSort}
        sortServer
        striped
        highlightOnHover
      />
    </div>
  );
};

export default BookingList;
