import React, { useState } from "react";
import { useGetBookingsQuery } from "../../redux/api/bookingApi";
import BookingForm from "./BookingForm";
import { Link } from "react-router-dom";
import AddUserForm from "./AddUserForm";

const BookingList = () => {
  const [showForm, setShowForm] = useState(false);
  const { data: bookings, isLoading, refetch } = useGetBookingsQuery();

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Bookings</h2>
        <button
          className="btn btn-primary"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Close Form" : "Add Booking"}
        </button>
      </div>

      {showForm && (
        <AddBookingForm setShowForm={setShowForm} refetch={refetch} />
      )}

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <table className="table table-bordered mt-4">
          <thead>
            <tr>
              <th>ID</th>
              <th>Traveler</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>From–To</th>
              <th>Date/Time</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {bookings?.map((b) => (
              <tr key={b._id}>
                <td>{b._id.slice(-5)}</td>
                <td>{b.travelerName}</td>
                <td>{b.email}</td>
                <td>{b.mobile}</td>
                <td>
                  {b.from} → {b.to}
                </td>
                <td>
                  {b.travelDate} {b.travelTime}
                </td>
                <td>{b.status}</td>
                <td>
                  <Link
                    to={`/admin/bookings/${b._id}`}
                    className="btn btn-sm btn-outline-primary"
                  >
                    View
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default BookingList;
