import { useState, useMemo } from "react";
import Header from "../Header";
import Footer from "../Footer";
import { useGetEmptyLegsQuery } from "../../redux/api/emptyLegApi";

export default function DealsList() {
    const { data, isLoading } = useGetEmptyLegsQuery();
    const emptyLegs = data?.emptyLegs || [];

    const [priceFilters, setPriceFilters] = useState(["All"]);

    // ---------- PRICE RANGE MATCH ----------
    function matchPriceRange(price, range) {
        switch (range) {
            case "₹ 0 - 1,00,000":
                return price >= 0 && price <= 100000;
            case "₹ 1,00,000 - 2,00,000":
                return price > 100000 && price <= 200000;
            case "₹ 2,00,000 - 3,00,000":
                return price > 200000 && price <= 300000;
            case "₹ 3,00,000+":
                return price > 300000;
            default:
                return true;
        }
    }

    const handleCheckboxChange = (e, value) => {
        const checked = e.target.checked;

        if (value === "All") return setPriceFilters(["All"]);

        const updated = checked
            ? [...priceFilters.filter((f) => f !== "All"), value]
            : priceFilters.filter((f) => f !== value);

        setPriceFilters(updated.length ? updated : ["All"]);
    };

    // ---------- FILTER EMPTY LEGS ----------
    const filteredLegs = useMemo(() => {
        let legs = emptyLegs;

        if (!priceFilters.includes("All")) {
            legs = legs.filter((leg) =>
                priceFilters.some((range) =>
                    matchPriceRange(leg.offerPrice || leg.exactPrice, range)
                )
            );
        }

        return legs;
    }, [emptyLegs, priceFilters]);

    return (
        <>
            <Header />

            <div className="inner-heading">
                <h1>Deals & Empty Leg Offers</h1>
            </div>

            <div className="listing-container">
                <div className="container-fluid">
                    <div className="listing">

                        {/* FILTERS */}
                        <div className="filter">
                            <div className="filter-box">
                                <h2>Price Range</h2>
                                <ul>
                                    {[
                                        "All",
                                        "₹ 0 - 1,00,000",
                                        "₹ 1,00,000 - 2,00,000",
                                        "₹ 2,00,000 - 3,00,000",
                                        "₹ 3,00,000+",
                                    ].map((range) => (
                                        <li key={range}>
                                            <label className="checkbox style-c">
                                                <input
                                                    type="checkbox"
                                                    checked={priceFilters.includes(range)}
                                                    onChange={(e) => handleCheckboxChange(e, range)}
                                                />
                                                <div className="checkbox__checkmark"></div>
                                            </label>
                                            {range}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>

                        {/* LISTING */}
                        <div className="listing-box">
                            {filteredLegs.length ? (
                                filteredLegs.map((leg) => (
                                    <div key={leg._id} className="listing-row">

                                        {/* Aircraft Image */}
                                        <div className="thumb">
                                            <img
                                                src={
                                                    leg.flightType?.images?.[0]?.url ||
                                                    `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
                                                }
                                                alt={leg.flightType?.name || "Aircraft"}
                                                height={200}
                                                width={200}
                                            />
                                        </div>

                                        {/* Content */}
                                        <div className="content">
                                            <h2>
                                                {leg.from} → {leg.to}
                                            </h2>

                                            <p><strong>Date:</strong> {leg.date}</p>
                                            <p><strong>Time:</strong> {leg.time}</p>

                                            {leg.flightType && (
                                                <>
                                                    <p><strong>Aircraft:</strong> {leg.flightType.name}</p>
                                                    <p><strong>Capacity:</strong> {leg.flightType.capacity} Persons</p>
                                                </>
                                            )}
                                        </div>

                                        {/* Price */}
                                        <div className="price">
                                            <div className="label">Offer</div>
                                            <div className="amount">
                                                ₹ {Number(leg.offerPrice || leg.exactPrice).toLocaleString()}
                                            </div>
                                            <div className="button">
                                                <a href="/contact" className="btn btn-fill">
                                                    Enquire Now
                                                </a>
                                            </div>
                                        </div>

                                    </div>

                                ))
                            ) : (
                                <div className="nomore-records">
                                    {isLoading ? "Loading deals..." : "No deals found"}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>

            <Footer />
        </>
    );
}
