import React, { useState, useEffect } from "react";
import {
  useCreateFlightCategoryMutation,
  useUpdateFlightCategoryMutation,
  useGetFlightCategoryByIdQuery,
} from "../../redux/api/flightCategoryApi";
import { useNavigate, useParams } from "react-router-dom";

const FlightCategoryForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [imageFile, setImageFile] = useState(null);

  const [createFlightCategory] = useCreateFlightCategoryMutation();
  const [updateFlightCategory] = useUpdateFlightCategoryMutation();
  const { data: editData } = useGetFlightCategoryByIdQuery(id, { skip: !id });

  useEffect(() => {
    if (editData?.flightCategory) {
      setName(editData.flightCategory.name);
      setDescription(editData.flightCategory.description);
    }
  }, [editData]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    if (imageFile) formData.append("image", imageFile);

    if (id) {
      await updateFlightCategory({ id, formData }).unwrap();
    } else {
      await createFlightCategory(formData).unwrap();
    }

    navigate("/flight-categories");
  };

  return (
    <form onSubmit={handleSubmit} encType="multipart/form-data">
      <div>
        <label>Name</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Description</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>
      </div>

      <div>
        <label>Image</label>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImageFile(e.target.files[0])}
          {...(!id && { required: true })}
        />
      </div>

      <button type="submit">{id ? "Update" : "Create"}</button>
    </form>
  );
};

export default FlightCategoryForm;
