// Price filter
export const matchPriceRange = (price, range) => {
    switch (range) {
        case "₹ 0 - 1,00,000":
            return price >= 0 && price <= 100000;
        case "₹ 1,00,000 - 2,00,000":
            return price > 100000 && price <= 200000;
        case "₹ 2,00,000 - 3,00,000":
            return price > 200000 && price <= 300000;
        case "₹ 3,00,000+":
            return price > 300000;
        default:
            return true;
    }
};

// Capacity filter
export const matchCapacity = (capacity, range) => {
    switch (range) {
        case "5 Persons":
            return capacity === 5;
        case "6 Persons":
            return capacity === 6;
        case "7 Persons":
            return capacity === 7;
        case "8 Persons":
            return capacity === 8;
        case "9+ Persons":
            return capacity >= 9;
        default:
            return true;
    }
};


export const calculateFlightPrice = (flight) => {
    // Find the Flying Cost head
    const flyingCostHead = flight.priceHeads?.find(
        (heads) => heads.head === "68a2c4d345a4c290c0959fe5"
    );

    const otherHeads =
        flight.priceHeads?.filter(
            (heads) => heads.head !== "68a2c4d345a4c290c0959fe5"
        ) || [];

    // Convert travel time (minutes → hours)
    const travelHours = (Number(flight.travelTime + 30) || 0) / 60;

    // Calculate
    const flyingCostPerHour = Number(flyingCostHead?.amount) || 0;
    const flyingCostTotal = flyingCostPerHour * travelHours;

    const otherCostTotal = otherHeads.reduce(
        (sum, head) => sum + (Number(head.amount) || 0),
        0
    );

    const basePrice = flyingCostTotal + otherCostTotal;
    const taxesAndFees = Math.round(basePrice * 0.12);

    // Final grand total (use same as display)
    const grandTotal = flyingCostTotal;

    return grandTotal;
};
