import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
  useCreateFlightCategoryMutation,
  useUpdateFlightCategoryMutation,
  useGetFlightCategoryByIdQuery,
} from "../../redux/api/flightCategoryApi";
import toast from "react-hot-toast";

const FlightCategoryForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEdit = Boolean(id);

  const { data: categoryData } = useGetFlightCategoryByIdQuery(id, {
    skip: !isEdit,
  });
  const [createCategory] = useCreateFlightCategoryMutation();
  const [updateCategory] = useUpdateFlightCategoryMutation();

  const [formState, setFormState] = useState({
    name: "",
    description: "",
    image: null,
  });
  const [previewImage, setPreviewImage] = useState(null);

  useEffect(() => {
    if (categoryData?.success && isEdit) {
      const { name, description, image } = categoryData.category;
      setFormState({ name, description, image: null });
      setPreviewImage(image?.url || null); // show existing image
    }
  }, [categoryData, isEdit]);

  const handleChange = (e) => {
    setFormState({ ...formState, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormState({ ...formState, image: file });
    setPreviewImage(URL.createObjectURL(file)); // preview new image
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", formState.name);
      formData.append("description", formState.description);

      if (formState.image instanceof File) {
        formData.append("image", formState.image);
      }

      if (isEdit) {
        await updateCategory({ id, formData }).unwrap(); // fixed key
        toast.success("Flight Category updated successfully");
      } else {
        await createCategory(formData).unwrap();
        toast.success("Flight Category created successfully");
      }

      navigate("/admin/flight-categories");
    } catch (err) {
      toast.error(err?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="container">
      <h2>{isEdit ? "Edit" : "Add"} Flight Category</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="form-group">
          <label>Name</label>
          <input
            name="name"
            value={formState.name}
            onChange={handleChange}
            className="form-control"
            placeholder="Enter category name"
            required
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            name="description"
            value={formState.description}
            onChange={handleChange}
            className="form-control"
            placeholder="Enter description"
            rows={4}
          />
        </div>

        <div className="form-group">
          <label>Image</label>
          <input
            type="file"
            name="image"
            onChange={handleFileChange}
            className="form-control"
            accept="image/*"
          />
          {previewImage && (
            <div className="mt-2">
              <img
                src={previewImage}
                alt="Preview"
                style={{
                  width: "120px",
                  height: "120px",
                  objectFit: "cover",
                  borderRadius: "4px",
                }}
              />
            </div>
          )}
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          {isEdit ? "Update" : "Create"}
        </button>
      </form>
    </div>
  );
};

export default FlightCategoryForm;
