// Price filter
export const matchPriceRange = (price, range) => {
    switch (range) {
        case "₹ 0 - 1,00,000":
            return price >= 0 && price <= 100000;
        case "₹ 1,00,000 - 2,00,000":
            return price > 100000 && price <= 200000;
        case "₹ 2,00,000 - 3,00,000":
            return price > 200000 && price <= 300000;
        case "₹ 3,00,000+":
            return price > 300000;
        default:
            return true;
    }
};

// Capacity filter
export const matchCapacity = (capacity, range) => {
    switch (range) {
        case "5 Persons":
            return capacity === 5;
        case "6 Persons":
            return capacity === 6;
        case "7 Persons":
            return capacity === 7;
        case "8 Persons":
            return capacity === 8;
        case "9+ Persons":
            return capacity >= 9;
        default:
            return true;
    }
};
