import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { useState, useMemo } from "react";
import { matchPriceRange, matchCapacity } from "../../utils/flightFilterHelper";

export default function Results() {
  const { results = {}, form = {} } = useSelector(
    (state) => state.search || {}
  );

  console.log("Results:", results);

  const [priceFilters, setPriceFilters] = useState(["All"]);
  const [capacityFilters, setCapacityFilters] = useState(["All"]);

  // ✅ Checkbox handler
  const handleCheckboxChange = (e, category, value) => {
    const checked = e.target.checked;

    if (category === "price") {
      value === "All"
        ? setPriceFilters(["All"])
        : setPriceFilters((prev) => {
            const updated = checked
              ? [...prev.filter((f) => f !== "All"), value]
              : prev.filter((f) => f !== value);
            return updated.length ? updated : ["All"];
          });
    }

    if (category === "capacity") {
      value === "All"
        ? setCapacityFilters(["All"])
        : setCapacityFilters((prev) => {
            const updated = checked
              ? [...prev.filter((f) => f !== "All"), value]
              : prev.filter((f) => f !== value);
            return updated.length ? updated : ["All"];
          });
    }
  };

  // ✅ Flight total price calculation (Oneway / Roundtrip)
  const calculateTotalPrice = (flight, isRoundTrip) => {
    const flyingCostHead = flight.priceHeads?.find(
      (h) => h.head === "68a2c4d345a4c290c0959fe5"
    );

    const flyingRate = Number(flyingCostHead?.amount) || 0;
    const travelHours = (Number(flight.travelTime + 30) || 0) / 60;
    const flightCost = flyingRate * travelHours;

    // ✅ Multiply for round trip
    return isRoundTrip ? flightCost * 2 : flightCost;
  };

  const filteredFlights = useMemo(() => {
    const isRoundTrip = form.tripType === "roundtrip";

    let flights = results?.flights || [];

    // Price filter
    if (!priceFilters.includes("All")) {
      flights = flights.filter((flight) =>
        priceFilters.some((range) =>
          matchPriceRange(calculateTotalPrice(flight, isRoundTrip), range)
        )
      );
    }

    // Capacity filter
    if (!capacityFilters.includes("All")) {
      flights = flights.filter((flight) =>
        capacityFilters.some((range) =>
          matchCapacity(Number(flight.capacity || 0), range)
        )
      );
    }

    return flights;
  }, [results, priceFilters, capacityFilters, form.tripType]);

  const isRoundTrip = form.tripType === "roundtrip";

  return (
    <>
      {/* ✅ Your full original JSX below — unchanged */}
      {/* Header */}
      <div className="header-container">
        <div className="container-fluid">
          <div className="header">
            <div className="logo">
              <Link to="/">
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/aayologo.png`}
                  alt="Aayo"
                />
              </Link>
            </div>
            <div className="nav">
              <ul>
                <li>
                  <Link className="active" to="/">
                    home
                  </Link>
                </li>
                <li>
                  <Link to="/features">features</Link>
                </li>
                <li>
                  <Link to="/about">about us</Link>
                </li>
                <li>
                  <Link to="/contact">contact us</Link>
                </li>
              </ul>
            </div>
            <div className="button">
              <Link className="btn btn-blank" to="/login">
                Sign in
              </Link>
              <Link className="btn btn-fill" to="/register">
                Sign up
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Heading */}
      <div className="inner-heading">
        <h1>Available Flights</h1>
      </div>

      {/* Route summary */}
      <div className="match-notfound">
        <div className="location-box available-flight">
          <div className="both-location">
            <div className="both-location-left">
              <div className="both-location-heading">
                {results.fromAirport?.iata_code}
              </div>
              <div className="both-location-fullname">
                ({results.fromAirport?.code})
              </div>
            </div>
            <div className="both-location-middle">
              <div className="icon">
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/icon-airplane-color.png`}
                  alt=""
                />
              </div>
            </div>
            <div className="both-location-right">
              <div className="both-location-heading">
                {results.toAirport?.iata_code}
              </div>
              <div className="both-location-fullname">
                ({results.toAirport?.code})
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Listing */}
      <div className="listing-container">
        <div className="container-fluid">
          <div className="listing">
            {/* Filters */}
            <div className="filter">
              <div className="filter-box">
                <h2>Price Range</h2>
                <ul>
                  {[
                    "All",
                    "₹ 0 - 1,00,000",
                    "₹ 1,00,000 - 2,00,000",
                    "₹ 2,00,000 - 3,00,000",
                    "₹ 3,00,000+",
                  ].map((range) => (
                    <li key={range}>
                      <label className="checkbox style-c">
                        <input
                          type="checkbox"
                          checked={priceFilters.includes(range)}
                          onChange={(e) =>
                            handleCheckboxChange(e, "price", range)
                          }
                        />
                        <div className="checkbox__checkmark"></div>
                      </label>{" "}
                      {range}
                    </li>
                  ))}
                </ul>
              </div>

              <div className="filter-box">
                <h2>Capacity</h2>
                <ul>
                  {[
                    "All",
                    "5 Persons",
                    "6 Persons",
                    "7 Persons",
                    "8 Persons",
                    "9+ Persons",
                  ].map((cap) => (
                    <li key={cap}>
                      <label className="checkbox style-c">
                        <input
                          type="checkbox"
                          checked={capacityFilters.includes(cap)}
                          onChange={(e) =>
                            handleCheckboxChange(e, "capacity", cap)
                          }
                        />
                        <div className="checkbox__checkmark"></div>
                      </label>{" "}
                      {cap}
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            {/* ✅ Flight list */}
            <div className="listing-box">
              {filteredFlights.length ? (
                filteredFlights.map((flight) => {
                  const total = calculateTotalPrice(flight, isRoundTrip);
                  return (
                    <div key={flight._id} className="listing-row">
                      <div className="thumb">
                        <img
                          src={
                            flight.images?.[0]?.url ||
                            `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
                          }
                          alt=""
                        />
                      </div>
                      <div className="content">
                        <h2>{flight.name}</h2>
                        {flight.description?.replace(/<[^>]+>/g, "")}
                      </div>
                      <div className="price">
                        <div className="label">Price</div>
                        <div className="amount">₹ {total.toLocaleString()}</div>
                        <div className="button">
                          <Link
                            to={`/flights/${flight._id}?from=${form.from}&to=${form.to}&pax=${form.pax}&date=${form.date}&time=${form.time}`}
                          >
                            View Details
                          </Link>
                        </div>
                      </div>
                    </div>
                  );
                })
              ) : (
                <div className="nomore-records">No matching flights found</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
