import React, { useState } from "react";
import DataTable from "react-data-table-component";
import {
  useGetLeadsQuery,
  useCloseLeadMutation,
} from "../../redux/api/leadApi";
import toast from "react-hot-toast";
import LeadRemarksModal from "./LeadRemarksModal"; // â¬…ï¸ Import the modal

const LeadList = () => {
  const [filters, setFilters] = useState({
    keyword: "",
    status: "",
    fromDate: "",
    toDate: "",
  });

  const { data, refetch } = useGetLeadsQuery(filters);
  const [closeLead] = useCloseLeadMutation();
  const [selectedLeadId, setSelectedLeadId] = useState(null); // For showing remarks modal

  const handleCloseLead = async (id) => {
    await closeLead(id);
    toast.success("Lead closed");
    refetch();
  };

  const columns = [
    { name: "Lead ID", selector: (row) => row._id },
    { name: "Name", selector: (row) => row.name },
    { name: "Email", selector: (row) => row.email },
    { name: "Phone", selector: (row) => row.phone },
    { name: "From-To", selector: (row) => `${row.fromPlace} - ${row.toPlace}` },
    { name: "Status", selector: (row) => row.status },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            data-bs-toggle="dropdown"
          >
            Actions
          </button>
          <ul className="dropdown-menu">
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleCloseLead(row._id)}
              >
                Close Lead
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => setSelectedLeadId(row._id)} // ðŸ‘ˆ Open modal
              >
                View Remarks
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => {
                  /* Show convert modal */
                }}
              >
                Convert to Booking
              </button>
            </li>
          </ul>
        </div>
      ),
    },
  ];

  return (
    <div className="container mt-4">
      <h4>Manage Leads</h4>

      {/* Add Lead button and filter can go here */}

      <DataTable columns={columns} data={data?.leads || []} pagination />

      {/* Show Remarks Modal */}
      {selectedLeadId && (
        <LeadRemarksModal
          leadId={selectedLeadId}
          onClose={() => setSelectedLeadId(null)}
        />
      )}
    </div>
  );
};

export default LeadList;
