import { useParams, Link, useLocation } from "react-router-dom";
import { useGetFlightByIdQuery } from "../../redux/api/flightApi";

export default function FlightDetail() {
  const { id } = useParams();
  const location = useLocation();
  const query = new URLSearchParams(location.search);

  const from = query.get("from");
  const to = query.get("to");
  const pax = query.get("pax");
  const { data, error, isLoading } = useGetFlightByIdQuery({
    id,
    from,
    to,
    pax,
  });

  if (isLoading) return <div className="loader">Loading...</div>;
  if (error) return <div className="error">Error loading flight details</div>;

  const flight = data?.flight;

  if (!flight) return <div className="error">Flight not found</div>;

  return (
    <>
      <div className="inner-heading">
        <h1>{flight.name}</h1>
      </div>

      <div className="container-fluid">
        <div className="flight-detail">
          <div className="thumb">
            <img
              src={
                flight.images?.[0]?.url ||
                `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
              }
              alt={flight.name}
              style={{ maxWidth: "100%", borderRadius: "8px" }}
            />
          </div>

          <div className="details">
            <h2>{flight.name}</h2>
            <p>{flight.description || "No description available"}</p>

            <ul className="stats">
              <li>
                <strong>Max Speed:</strong> {flight.maxSpeed} km/hr
              </li>
              <li>
                <strong>Capacity:</strong> {flight.capacity} Persons
              </li>
              <li>
                <strong>Baggage:</strong> {flight.baggage || "N/A"} kg
              </li>
              <li>
                <strong>Base Location:</strong> {flight.baseLocation?.name}
              </li>
              <li>
                <strong>Price:</strong> â‚¹ {flight.price?.toLocaleString()}
              </li>
              <li>
                <strong>Estimated Travel Time:</strong>{" "}
                {flight.travelTime || "N/A"} Minutes
              </li>
            </ul>

            <div className="actions">
              <Link to="/" className="btn btn-blank">
                Back
              </Link>
              <button className="btn btn-fill">Proceed to Booking</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
