import { useState } from "react";
import toast from "react-hot-toast";
import axios from "../../utils/axios";
import PublicLayout from "../../components/layout/PublicLayout";

/* ================= S3 UPLOAD ================= */
const uploadToS3 = async (file, folder) => {
  const { data } = await axios.get(
    `/s3/presigned-url?fileType=${encodeURIComponent(
      file.type
    )}&folder=${encodeURIComponent(folder)}`
  );

  const res = await fetch(data.uploadUrl, {
    method: "PUT",
    body: file,
  });

  if (!res.ok) throw new Error("S3 upload failed");
  return data.fileUrl;
};

const ModelRegister = () => {
  const [loading, setLoading] = useState(false);

  /* ================= FORM STATE ================= */
  const [form, setForm] = useState({
    /* ACCOUNT */
    username: "",
    password: "",
    email: "",

    /* PROFILE HEADER */
    displayName: "",
    age: "",
    tagline: "",
    basedIn: "",
    nationality: "",

    /* SERVICES */
    services: [],
    placeOfService: [],
    profileType: "",

    /* INFO CARD */
    infoAge: "",
    height: "",
    weight: "",
    cupSize: "",
    price1h: "",
    currency: "",

    /* MY PROFILE */
    ethnicity: "",
    bodyType: "",
    hairColor: "",
    eyes: "",
    breast: "",
    pubicHair: "",
    meetingWith: [],
    languages: "",
    locationText: "",

    /* RATES */
    rates: {
      rate30Out: "",
      rate30In: "",
      rate1hOut: "",
      rate1hIn: "",
      rate2hOut: "",
      rate2hIn: "",
      note: "",
    },

    /* AVAILABILITY */
    days: [],
    availabilityText: "",

    /* CONTACT */
    phone: "",
    website: "",
    snapchat: "",
    preferredContact: "",

    /* ABOUT */
    aboutMe: "",

    acceptTerms: false,
  });

  const [profilePhotos, setProfilePhotos] = useState([]);
  const [galleryPhotos, setGalleryPhotos] = useState([]);

  /* ================= HANDLERS ================= */
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox") {
      setForm((p) => {
        const arr = p[name] || [];
        return {
          ...p,
          [name]: checked ? [...arr, value] : arr.filter((v) => v !== value),
        };
      });
    } else {
      setForm((p) => ({ ...p, [name]: value }));
    }
  };

  const handleRateChange = (key, value) => {
    setForm((p) => ({
      ...p,
      rates: { ...p.rates, [key]: value },
    }));
  };

  /* ================= SUBMIT ================= */
  const submit = async (e) => {
    e.preventDefault();

    if (!form.acceptTerms) {
      toast.error("Please accept terms");
      return;
    }

    try {
      setLoading(true);

      /* UPLOAD IMAGES */
      const profileImage =
        profilePhotos.length > 0
          ? { url: await uploadToS3(profilePhotos[0], "models/profile") }
          : null;

      const portfolio = [];
      for (const img of galleryPhotos) {
        portfolio.push({
          url: await uploadToS3(img, "models/portfolio"),
        });
      }

      /* PAYLOAD */
      const payload = {
        username: form.username,
        password: form.password,
        email: form.email,

        stageName: form.displayName,
        age: Number(form.age),
        tagline: form.tagline,
        city: form.basedIn,
        nationality: form.nationality,

        services: form.services,
        availableFor: form.placeOfService,
        profileType: form.profileType,

        heightCm: form.height,
        weightKg: form.weight,
        cupSize: form.cupSize,

        ethnicity: form.ethnicity,
        bodyType: form.bodyType,
        hairColor: form.hairColor,
        eyeColor: form.eyes,
        breast: form.breast,
        pubicHair: form.pubicHair,
        meetingWith: form.meetingWith,
        languages: form.languages
          ? form.languages.split(",").map((l) => l.trim())
          : [],
        locationText: form.locationText,

        rates: {
          ...form.rates,
          price1h: form.price1h,
          currency: form.currency,
        },

        availableDays: form.days,
        availabilityText: form.availabilityText,

        phone: form.phone,
        website: form.website,
        snapchat: form.snapchat,
        preferredContact: form.preferredContact,

        aboutMe: form.aboutMe,

        profileImage,
        portfolio,
      };

      await axios.post("/models/register", payload);

      toast.success("Profile submitted for review");
    } catch (err) {
      toast.error(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  /* ================= JSX ================= */
  return (
    <PublicLayout>
      <main className="single-profile">
        <section className="sp-title-bar">
          <div className="sp-inner">
            <h1 className="sp-main-title">Model Registration</h1>
            <p className="sp-subline">
              Submit your details to create a verified DubaiSociete profile
            </p>
          </div>
        </section>

        <section className="sp-details">
          <div className="sp-inner">
            <form className="reg-form" onSubmit={submit}>
              {/* ACCOUNT */}
              <div className="sp-card reg-card">
                <h2 className="reg-section-title">Account details</h2>
                <input
                  name="username"
                  placeholder="Username"
                  required
                  onChange={handleChange}
                />
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  required
                  onChange={handleChange}
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  required
                  onChange={handleChange}
                />
              </div>

              {/* PROFILE HEADER */}
              <div className="sp-card reg-card">
                <h2 className="reg-section-title">Profile header</h2>
                <input
                  name="displayName"
                  placeholder="Display name"
                  required
                  onChange={handleChange}
                />
                <input
                  type="number"
                  name="age"
                  min="18"
                  placeholder="Age"
                  required
                  onChange={handleChange}
                />
                <input
                  name="tagline"
                  placeholder="Tagline"
                  onChange={handleChange}
                />
                <select name="basedIn" onChange={handleChange}>
                  <option value="">Select area</option>
                  <option>Dubai Marina</option>
                  <option>JBR</option>
                  <option>Downtown</option>
                  <option>Business Bay</option>
                </select>
              </div>

              {/* SERVICES */}
              <div className="sp-card reg-card">
                <h2 className="reg-section-title">Services</h2>
                {["gfe", "owo", "massage", "couples", "threesome"].map((s) => (
                  <label key={s}>
                    <input
                      type="checkbox"
                      name="services"
                      value={s}
                      onChange={handleChange}
                    />{" "}
                    {s}
                  </label>
                ))}
              </div>

              {/* PLACE OF SERVICE */}
              <div className="sp-card reg-card">
                <h2 className="reg-section-title">Place of service</h2>
                <label>
                  <input
                    type="checkbox"
                    name="placeOfService"
                    value="incall"
                    onChange={handleChange}
                  />{" "}
                  Incall
                </label>
                <label>
                  <input
                    type="checkbox"
                    name="placeOfService"
                    value="outcall"
                    onChange={handleChange}
                  />{" "}
                  Outcall
                </label>
              </div>

              {/* INFO CARD */}
              <div className="sp-card reg-card">
                <h2 className="reg-section-title">Info card details</h2>
                <input
                  name="height"
                  placeholder="Height"
                  onChange={handleChange}
                />
                <input
                  name="weight"
                  placeholder="Weight"
                  onChange={handleChange}
                />
                <select name="cupSize" onChange={handleChange}>
                  <option value="">Cup size</option>
                  <option>B</option>
                  <option>C</option>
                  <option>D</option>
                </select>
              </div>

              {/* ABOUT */}
              <div className="sp-card reg-card">
                <h2 className="reg-section-title">About me</h2>
                <textarea name="aboutMe" rows="6" onChange={handleChange} />
              </div>

              {/* PHOTOS */}
              <div className="sp-card reg-card">
                <h2 className="reg-section-title">Photos</h2>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={(e) => setProfilePhotos(Array.from(e.target.files))}
                />
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={(e) => setGalleryPhotos(Array.from(e.target.files))}
                />
              </div>

              {/* TERMS */}
              <div className="sp-card reg-card">
                <label>
                  <input
                    type="checkbox"
                    name="acceptTerms"
                    onChange={(e) =>
                      setForm((p) => ({ ...p, acceptTerms: e.target.checked }))
                    }
                  />
                  I confirm I am over 18
                </label>
                <button className="reg-submit-btn" disabled={loading}>
                  {loading ? "Submitting..." : "Submit profile for review"}
                </button>
              </div>
            </form>
          </div>
        </section>
      </main>
    </PublicLayout>
  );
};

export default ModelRegister;
