import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate, useLocation } from "react-router-dom";
import { logout } from "../../redux/slices/authSlice";
import Header from "../Header";
import Footer from "../Footer";
import { useGetBookingsQuery } from "../../redux/api/bookingApi";

export default function Dashboard() {
  const { userInfo } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();

  // Tabs
  const [activeTab, setActiveTab] = useState("pending"); // pending | confirmed | cancelled

  // Date filter
  const [filters, setFilters] = useState({
    fromDate: "",
    toDate: "",
  });

  const { data, isLoading } = useGetBookingsQuery();

  const handleLogout = () => {
    dispatch(logout());
    navigate("/");
  };

  if (!userInfo) {
    return (
      <div className="p-5 text-center">
        <h2>Please login to access dashboard</h2>
      </div>
    );
  }

  // All bookings
  const bookings = data?.bookings || [];

  // --- Only keep bookings that belong to current user ---
  // Support common shapes: booking.user (id or object), booking.userId, booking.user._id
  console.log("userInfo:", userInfo);
  const myBookings = bookings.filter((b) => {
    console.log("Checking booking:", b);
    const currentUserId = userInfo?.user?.id;

    if (!currentUserId) return false;

    // booking.user could be an id string, or an object with _id
    if (b.user) {
      if (typeof b.user === "string" && b.user === currentUserId) return true;
      if (
        typeof b.user === "object" &&
        (b.user._id === currentUserId || b.user.id === currentUserId)
      )
        return true;
    }

    // booking.userId could also exist
    if (b.userId) {
      if (typeof b.userId === "string" && b.userId === currentUserId)
        return true;
      if (
        typeof b.userId === "object" &&
        (b.userId._id === currentUserId || b.userId.id === currentUserId)
      )
        return true;
    }

    // Some bookings might store createdBy or customer fields
    if (b.createdBy) {
      if (typeof b.createdBy === "string" && b.createdBy === currentUserId)
        return true;
      if (
        typeof b.createdBy === "object" &&
        (b.createdBy._id === currentUserId || b.createdBy.id === currentUserId)
      )
        return true;
    }

    return false;
  });

  // Filter bookings by tab (status) - case-insensitive
  let filteredBookings = myBookings.filter((b) => {
    const status = (b.status || "").toString().toLowerCase();
    return status === activeTab.toLowerCase();
  });

  // Apply date range filter
  if (filters.fromDate || filters.toDate) {
    filteredBookings = filteredBookings.filter((b) => {
      // guard if travelDate is missing
      if (!b.travelDate) return false;
      const bookingDate = new Date(b.travelDate);

      // Normalize user-provided dates (they are yyyy-mm-dd strings)
      const fromDate = filters.fromDate ? new Date(filters.fromDate) : null;
      const toDate = filters.toDate ? new Date(filters.toDate) : null;

      // Compare only dates (ignore time)
      if (fromDate) {
        // set time to start of day for inclusive compare
        fromDate.setHours(0, 0, 0, 0);
        if (bookingDate < fromDate) return false;
      }
      if (toDate) {
        // set time to end of day for inclusive compare
        toDate.setHours(23, 59, 59, 999);
        if (bookingDate > toDate) return false;
      }
      return true;
    });
  }

  // Handle filter input
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    // Filtering handled automatically in render logic
  };

  return (
    <>
      <Header />

      <div className="inner-heading">
        <h1>My Bookings</h1>
      </div>

      <div className="mybookings-container container">
        <div className="mybookings-left">
          <ul>
            {["pending", "confirmed", "cancelled"].map((tab) => (
              <li key={tab}>
                <a
                  href="#"
                  className={activeTab === tab ? "active" : ""}
                  onClick={(e) => {
                    e.preventDefault();
                    setActiveTab(tab);
                  }}
                >
                  {tab.charAt(0).toUpperCase() + tab.slice(1)}
                </a>
              </li>
            ))}
          </ul>
        </div>

        <div className="mybookings-content">
          {/* Filter Form */}
          <form className="filter" onSubmit={handleFilterSubmit}>
            <div className="filter-col">
              <label htmlFor="fromDate">From Date</label>
              <input
                type="date"
                name="fromDate"
                value={filters.fromDate}
                onChange={handleFilterChange}
                className="icondate"
              />
            </div>

            <div className="filter-col">
              <label htmlFor="toDate">To Date</label>
              <input
                type="date"
                name="toDate"
                value={filters.toDate}
                onChange={handleFilterChange}
                className="icondate"
              />
            </div>

            <div className="filter-col-button">
              <label>&nbsp;</label>
              <input type="submit" value="Search" />
            </div>
          </form>

          {/* Bookings List */}
          <div className="mybookings-listing active">
            {isLoading ? (
              <p>Loading bookings...</p>
            ) : filteredBookings.length > 0 ? (
              filteredBookings.map((booking) => (
                <div className="mybookings-col" key={booking._id}>
                  <div className="thumb">
                    <img
                      src={
                        booking?.flightType?.images?.[0]?.url ||
                        "/assets/images/thumb-learjet45.jpg"
                      }
                      alt="Flight"
                    />
                  </div>
                  <div className="content">
                    <h2>
                      {booking.fromPlace?.name || booking.fromPlace}{" "}
                      <div className="icon">
                        <img
                          src="/assets/images/icon-airplane-color.png"
                          alt="Airplane"
                        />
                      </div>{" "}
                      {booking.toPlace?.name || booking.toPlace}{" "}
                      <span
                        className={`status ${
                          booking.status === "confirmed"
                            ? "status-success"
                            : booking.status === "cancelled"
                            ? "status-danger"
                            : "status-warning"
                        }`}
                      >
                        {booking.status}
                      </span>
                    </h2>
                    <p>
                      <strong>Booking ID</strong> - {booking._id.slice(-6)}
                    </p>
                    <p>
                      <strong>Booking Date</strong> -{" "}
                      {new Date(booking.travelDate).toLocaleDateString()} at{" "}
                      {booking.travelTime}
                    </p>
                  </div>
                </div>
              ))
            ) : (
              <p>No {activeTab} bookings found.</p>
            )}
          </div>
        </div>
      </div>

      <div className="text-center mt-4">
        <button onClick={handleLogout} className="btn btn-outline-danger">
          Logout
        </button>
      </div>

      <Footer />
    </>
  );
}
