import { useParams, useLocation, useNavigate } from "react-router-dom";
import { useGetFlightByIdQuery } from "../../redux/api/flightApi";
import Header from "../Header";
import Footer from "../Footer";
import { useState } from "react";
import { useEffect } from "react";

export default function FlightDetail() {
  const [amount, setAmount] = useState(0);

  const navigate = useNavigate();
  const { id } = useParams();
  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const from = query.get("from");
  const to = query.get("to");
  const pax = query.get("pax");
  const travelDate = query.get("date");
  const travelTime = query.get("time");

  const { data, error, isLoading } = useGetFlightByIdQuery({
    id,
    from,
    to,
    pax,
  });

  // ✅ move useEffect here (BEFORE any conditional return)
  useEffect(() => {
    if (!data?.flight?.priceHeads?.length || !data?.flight?.travelTime) return;

    const flight = data.flight;

    let flyingCostHead = flight.priceHeads?.find(
      (heads) => heads.head?.headName?.toLowerCase() === "flying cost"
    );

    const otherHeads =
      flight.priceHeads?.filter(
        (heads) => heads.head?.headName?.toLowerCase() !== "flying cost"
      ) || [];

    const travelHours = (Number(flight.travelTime) || 0) / 60;
    const flyingCostPerHour = Number(flyingCostHead?.amount) || 0;
    const flyingCostTotal = flyingCostPerHour * travelHours;

    const otherCostTotal = otherHeads.reduce(
      (sum, head) => sum + (Number(head.amount) || 0),
      0
    );

    const basePrice = flyingCostTotal + otherCostTotal;
    const taxesAndFees = Math.round(basePrice * 0.12);
    const grandTotal = basePrice + taxesAndFees;

    setAmount(grandTotal);
  }, [data]);

  if (isLoading) return <div className="loader">Loading...</div>;
  if (error) return <div className="error">Error loading flight details</div>;

  const flight = data?.flight;
  const fromAirport = data?.fromAirport;
  const toAirport = data?.toAirport;
  console.log("Flight Detail Data:", data);
  if (!flight) return <div className="error">Flight not found</div>;

  // ✅ Calculate total price using priceHeads (like in Results.jsx)
  // const basePrice = (flight.priceHeads || []).reduce(
  //   (sum, head) => sum + (Number(head.amount) || 0),
  //   0
  // );

  // const taxesAndFees = Math.round(basePrice * 0.12);
  // const grandTotal = basePrice + taxesAndFees;

  const handleEnquiryClick = () => {
    const bookingParams = `?flightId=${id}&from=${from}&to=${to}&pax=${pax}&date=${travelDate}&time=${travelTime}`;
    const isLoggedIn = localStorage.getItem("token");

    if (isLoggedIn) {
      navigate(`/enquiry${bookingParams}`, { state: { amount } });
    } else {
      navigate(`/login${bookingParams}`, { state: { amount } });
    }
  };

  return (
    <>
      <Header />
      <div className="inner-heading">
        <h1>Flight Details</h1>
      </div>

      <div className="summary-container mt-30">
        <div className="container-fluid">
          <div className="summary-box-container">
            <div className="summary">
              {/* ====== Left Section ====== */}
              <div className="summary-content">
                <div className="summary-banner">
                  <img
                    src={
                      flight.images?.[0]?.url ||
                      `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
                    }
                    alt={flight.name}
                  />
                </div>

                <div className="summary-header">
                  <div className="summary-headerleft">
                    <h2>{flight.name}</h2>
                    <div className="details">
                      <ul>
                        <li>
                          <div className="icon">
                            <img
                              src="/assets/images/icon-calendar-small.png"
                              alt="Calendar"
                            />
                          </div>
                          {travelDate}
                        </li>
                        <li>
                          <div className="icon">
                            <img
                              src="/assets/images/icon-time-small.png"
                              alt="Time"
                            />
                          </div>
                          {travelTime}
                        </li>
                        <li>
                          <div className="icon">
                            <img
                              src="/assets/images/icon-user.png"
                              alt="User"
                            />
                          </div>
                          {pax} Passengers
                        </li>
                      </ul>
                    </div>
                  </div>

                  <div className="summary-headerright">
                    <div className="stats">
                      <div className="stats-col">
                        <div className="icon">
                          <img
                            src="/assets/images/icon-speed.png"
                            alt="Speed"
                          />
                        </div>
                        <div className="heading">Speed</div>
                        <p>{flight.maxSpeed} km/hr</p>
                      </div>
                      <div className="stats-col">
                        <div className="icon">
                          <img
                            src="/assets/images/icon-person.png"
                            alt="Person"
                          />
                        </div>
                        <div className="heading">Passengers</div>
                        <p>{flight.capacity} Person</p>
                      </div>
                      <div className="stats-col">
                        <div className="icon">
                          <img src="/assets/images/icon-time.png" alt="Time" />
                        </div>
                        <div className="heading">Travel Time</div>
                        <p>
                          {Math.floor(flight.travelTime / 60)} hr{" "}
                          {flight.travelTime % 60} min
                        </p>
                      </div>
                      <div className="stats-col">
                        <div className="icon">
                          <img
                            src="/assets/images/icon-luggage.png"
                            alt="Luggage"
                          />
                        </div>
                        <div className="heading">Luggage</div>
                        <p>{flight.baggage || "N/A"} kg</p>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="summary-guidelines">
                  <h3>Mandatory Guidelines for all travellers</h3>
                  <p>
                    Please arrive 30 minutes before departure. Carry a valid
                    government ID. Ensure baggage does not exceed allowed limit.
                  </p>
                </div>
              </div>

              {/* ====== Right Section ====== */}
              <div className="summary-details">
                <h2>Ride Details</h2>

                <div className="summary-detail-box">
                  <h3>Ride Route</h3>
                  <div className="loc-info">
                    {/* ✅ Step 1: Show Base → From leg if different */}
                    {flight.baseLocation &&
                      flight.baseLocation !== fromAirport?.iata_code && (
                        <>
                          <div className="loc-info-col">
                            <div className="icon">
                              <span className="material-icons-outlined">
                                flight_takeoff
                              </span>
                            </div>
                            <p>
                              <strong>Base Location:</strong>{" "}
                              {flight.baseLocation}
                            </p>
                          </div>
                          <div className="loc-info-col">
                            <div className="icon">
                              <span className="material-icons-outlined">
                                location_on
                              </span>
                            </div>
                            <p>
                              {fromAirport?.name}, {fromAirport?.city},{" "}
                              {fromAirport?.country}
                            </p>
                          </div>

                          {/* ✅ Step 3: To → Base (only if base is different) */}
                          {flight.baseLocation &&
                            flight.baseLocation?.iata_code !==
                              toAirport?.iata_code && (
                              <>
                                <div className="loc-info-col">
                                  <div className="icon">
                                    <span className="material-icons-outlined">
                                      flight_takeoff
                                    </span>
                                  </div>
                                  <p>
                                    {toAirport?.name} ({toAirport?.iata_code}),{" "}
                                    {toAirport?.city}, {toAirport?.country}
                                  </p>
                                </div>
                                <div className="loc-info-col">
                                  <div className="icon">
                                    <span className="material-icons-outlined">
                                      flight_land
                                    </span>
                                  </div>
                                  <p>
                                    <strong>Back to Base:</strong>{" "}
                                    {flight.baseLocation?.name} (
                                    {flight.baseLocation?.iata_code}),{" "}
                                    {flight.baseLocation?.city},{" "}
                                    {flight.baseLocation?.country}
                                  </p>
                                </div>
                              </>
                            )}
                          {/* <div className="route-separator">
                            <span className="material-icons-outlined">
                              arrow_downward
                            </span>
                          </div> */}
                        </>
                      )}

                    {/* ✅ Step 2: Show From → To leg */}
                    <div className="loc-info-col">
                      <div className="icon">
                        <span className="material-icons-outlined">
                          flight_takeoff
                        </span>
                      </div>
                      <p>
                        {fromAirport?.name}, {fromAirport?.city},{" "}
                        {fromAirport?.country}
                      </p>
                    </div>
                    <div className="loc-info-col">
                      <div className="icon">
                        <span className="material-icons-outlined">
                          flight_land
                        </span>
                      </div>
                      <p>
                        {toAirport?.name}, {toAirport?.city},{" "}
                        {toAirport?.country}
                      </p>
                    </div>
                  </div>
                </div>

                {/* <div className="summary-detail-box">
                  <h3>
                    {fromAirport?.name} - {toAirport?.name}
                  </h3>
                  <div className="loc-info">
                    <div className="loc-info-col">
                      <div className="icon">
                        <span className="material-icons-outlined">
                          location_on
                        </span>
                      </div>
                      <p>
                        {fromAirport?.name}, {fromAirport?.city},{" "}
                        {fromAirport?.country}
                      </p>
                    </div>
                    <div className="loc-info-col">
                      <div className="icon">
                        <span className="material-icons-outlined">
                          location_on
                        </span>
                      </div>
                      <p>
                        {toAirport?.name}, {toAirport?.city},{" "}
                        {toAirport?.country}
                      </p>
                    </div>
                  </div>
                </div> */}

                <div className="summary-detail-box">
                  <h3>Distance</h3>
                  <div className="info">
                    <div className="info-col">
                      <div className="icon">
                        <span className="material-icons-outlined">
                          location_on
                        </span>
                      </div>
                      <p>
                        <strong>Total Distance:</strong> {flight.distance} KM
                      </p>
                    </div>
                    <div className="info-col">
                      <div className="icon">
                        <span className="material-icons-outlined">
                          schedule
                        </span>
                      </div>
                      <p>
                        <strong>Total Duration:</strong>{" "}
                        {Math.floor(flight.travelTime / 60)} hr{" "}
                        {flight.travelTime % 60} min
                      </p>
                    </div>
                  </div>
                </div>

                {/* ✅ Dynamic Bill Section */}

                {/* <h2>Bill Details</h2>
                <div className="summary-detail-box">
                  {flight.priceHeads?.length ? (
                    flight.priceHeads.map((head, index) => (
                      <div className="summary-detail" key={index}>
                        <div className="heading">Base Fare</div>
                        <div className="text">
                          <span className="material-icons-outlined">
                            currency_rupee
                          </span>{" "}
                          {Number(head.amount).toLocaleString()}
                        </div>
                      </div>
                    ))
                  ) : (
                    <p>No price breakdown available.</p>
                  )}

                  <div className="summary-detail">
                    <div className="heading">Taxes & Fees (12%)</div>
                    <div className="text">
                      <span className="material-icons-outlined">
                        currency_rupee
                      </span>{" "}
                      {taxesAndFees.toLocaleString()}
                    </div>
                  </div>

                  <div className="summary-sep"></div>

                  <div className="summary-detail grand-total">
                    <div className="heading">Grand Total</div>
                    <div className="text">
                      <span className="material-icons-outlined">
                        currency_rupee
                      </span>{" "}
                      {grandTotal.toLocaleString()}
                    </div>
                  </div>
                </div> */}

                {/* ✅ Bill Details Section */}
                <h2>Bill Details</h2>
                <div className="summary-detail-box">
                  {(() => {
                    // Find the 'Flying Cost' head
                    let flyingCostHead = flight.priceHeads?.find(
                      (heads) =>
                        heads.head?.headName?.toLowerCase() === "flying cost"
                    );

                    // If your API returns only head IDs, replace the above check with ID comparison, e.g.:
                    // let flyingCostHead = flight.priceHeads?.find(head => head.head === "FLYING_COST_ID");

                    const otherHeads =
                      flight.priceHeads?.filter(
                        (heads) =>
                          heads.head?.headName?.toLowerCase() !== "flying cost"
                      ) || [];

                    // ✅ Convert travel time from minutes → hours
                    const travelHours = (Number(flight.travelTime) || 0) / 60;

                    // ✅ Calculate costs
                    const flyingCostPerHour =
                      Number(flyingCostHead?.amount) || 0;
                    const flyingCostTotal = flyingCostPerHour * travelHours;

                    const otherCostTotal = otherHeads.reduce(
                      (sum, head) => sum + (Number(head.amount) || 0),
                      0
                    );

                    const basePrice = flyingCostTotal + otherCostTotal;
                    const taxesAndFees = Math.round(basePrice * 0.12);
                    const grandTotal = basePrice + taxesAndFees;

                    return (
                      <>
                        {/* Show all price heads breakdown */}
                        {flight.priceHeads?.length ? (
                          <>
                            {flyingCostHead && (
                              <div className="summary-detail">
                                <div className="heading">Flying Cost</div>
                                <div className="text">
                                  <span className="material-icons-outlined">
                                    currency_rupee
                                  </span>{" "}
                                  {/* {flyingCostPerHour.toLocaleString()} ×{" "}
                                  {travelHours.toFixed(2)} hr = */}{" "}
                                  <strong>
                                    {parseFloat(flyingCostTotal)
                                      .toFixed(2)
                                      .toLocaleString()}
                                  </strong>
                                </div>
                              </div>
                            )}

                            {otherHeads.map((head, index) => (
                              <div className="summary-detail" key={index}>
                                <div className="heading">
                                  {head.headName || "Other Charge"}
                                </div>
                                <div className="text">
                                  <span className="material-icons-outlined">
                                    currency_rupee
                                  </span>{" "}
                                  {Number(head.amount).toLocaleString()}
                                </div>
                              </div>
                            ))}
                          </>
                        ) : (
                          <p>No price breakdown available.</p>
                        )}

                        <div className="summary-detail">
                          <div className="heading">Taxes & Fees (12%)</div>
                          <div className="text">
                            <span className="material-icons-outlined">
                              currency_rupee
                            </span>{" "}
                            {taxesAndFees.toLocaleString()}
                          </div>
                        </div>

                        <div className="summary-sep"></div>

                        <div className="summary-detail">
                          <div className="heading">Estimated Cost</div>
                          <div className="text">
                            <span className="material-icons-outlined">
                              currency_rupee
                            </span>{" "}
                            {grandTotal.toLocaleString()}
                          </div>
                        </div>
                      </>
                    );
                  })()}
                </div>

                <div className="summary-button">
                  <input
                    type="button"
                    className="btn btn-fill"
                    onClick={handleEnquiryClick}
                    value="Book Now"
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}
