import React, { useState } from "react";
import {
  useGetFlightsQuery,
  useDeleteFlightMutation,
  useToggleFlightStatusMutation,
} from "../../redux/api/flightApi";
import FlightForm from "./FlightForm";
import toast from "react-hot-toast";
import DataTable from "react-data-table-component";

const FlightList = () => {
  const { data, isLoading, refetch } = useGetFlightsQuery();
  const [deleteFlight] = useDeleteFlightMutation();
  const [toggleStatus] = useToggleFlightStatusMutation();
  const [editingFlight, setEditingFlight] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure?")) {
      try {
        await deleteFlight(id).unwrap();
        toast.success("Flight deleted successfully");
        refetch();
      } catch (err) {
        toast.error("Failed to delete flight");
      }
    }
  };

  const handleToggle = async (id) => {
    await toggleStatus(id);
    refetch();
  };

  const columns = [
    {
      name: "Flight",
      selector: (row) => row.flightType.name,
      sortable: true,
    },
    {
      name: "From – To",
      selector: (row) => `${row.from} – ${row.to}`,
    },
    {
      name: "Date",
      selector: (row) => row.date.split("T")[0],
    },
    {
      name: "Time",
      selector: (row) => row.time,
    },
    {
      name: "Status",
      selector: (row) => (
        <span
          className={`badge ${
            row.status === "active" ? "bg-success" : "bg-secondary"
          }`}
        >
          {row.status}
        </span>
      ),
    },
    {
      name: "Actions",
      cell: (row) => (
        <div className="dropdown">
          <button
            className="btn btn-sm btn-secondary dropdown-toggle"
            type="button"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Actions
          </button>
          <ul className="dropdown-menu">
            <li>
              <button
                className="dropdown-item"
                onClick={() => {
                  setEditingFlight(row);
                  setShowForm(true);
                }}
              >
                Edit
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleDelete(row._id)}
              >
                Delete
              </button>
            </li>
            <li>
              <button
                className="dropdown-item"
                onClick={() => handleToggle(row._id)}
              >
                Toggle Status
              </button>
            </li>
          </ul>
        </div>
      ),
      ignoreRowClick: true,
      allowoverflow: true,
      button: true,
    },
  ];

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h4>Flight List</h4>

        <button
          className="btn btn-primary mb-3"
          onClick={() => {
            setEditingFlight(null); // Clear edit mode for new flight
            setShowForm(!showForm);
          }}
        >
          {showForm ? "Close Form" : "Add New Flight"}
        </button>
      </div>

      {showForm && (
        <FlightForm
          editingFlight={editingFlight}
          onSuccess={() => {
            setEditingFlight(null);
            setShowForm(false);
            refetch();
          }}
        />
      )}

      <DataTable
        columns={columns}
        data={data?.flights || []}
        progressPending={isLoading}
        pagination
        highlightOnHover
        responsive
        striped
        noDataComponent="No flights found"
      />
    </div>
  );
};

export default FlightList;
