import { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useCreateEnquiryMutation } from "../../redux/api/bookingApi";
import Header from "../Header";
import Footer from "../Footer";
import { useSelector } from "react-redux";
import { useGetFlightTypeByIdQuery } from "../../redux/api/flightTypeApi";

export default function EnquiryForm() {
  // User info from Redux store
  const { userInfo } = useSelector((state) => state.auth);
  console.log("Enquiry Form User Info:", userInfo);
  const navigate = useNavigate();
  const location = useLocation();
  //const flightId = new URLSearchParams(location.search).get("flightId");
  const query = new URLSearchParams(location.search);
  //console.log("Flight Detail Query Params:", Array.from(query.entries()));
  const flightId = query.get("flightId");
  const from = query.get("from");
  const to = query.get("to");
  const pax = query.get("pax");
  const travelDate = query.get("date");
  const travelTime = query.get("time");
  const { data: flightData, isLoading: flightLoading } =
    useGetFlightTypeByIdQuery(flightId);
  console.log("Flight Data for ID", flightId, ":", flightData);
  const [formData, setFormData] = useState({
    name: userInfo?.user?.name || "",
    email: userInfo?.user?.email || "",
    phone: userInfo?.user?.phone || "",
    fromPlace: from || "",
    toPlace: to || "",
    travelDate: travelDate || "",
    travelTime: travelTime || "",
    remarks: "",
    pax: pax || 1,
  });

  const [createEnquiry, { isLoading, error, isSuccess, isError }] =
    useCreateEnquiryMutation();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  console.log("Enquiry Form Flight ID:", formData);
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log("Submitting Enquiry with data:", {
        ...formData,
        flightType: flightId,
        totalAmount: 0,
      });
      await createEnquiry({
        ...formData,
        flightType: flightId,
        totalAmount: 0,
      }).unwrap();
      alert("Enquiry submitted successfully!");
      navigate("/");
    } catch (err) {
      console.error(err);
      alert("Failed to submit enquiry");
    }
  };

  return (
    <>
      <Header />
      <div className="login-sec-container enquiry-sec-container">
        <div className="login-sec-form">
          <div className="form-flex mt-30">
            <form className="form" onSubmit={handleSubmit}>
              <div className="heading mb-30">Review Booking</div>

              <div className="input-box">
                <label htmlFor="name">What’s your good name?</label>
                <div className="input-flex">
                  <input
                    type="text"
                    name="name"
                    placeholder="John Doe"
                    value={formData.name}
                    onChange={handleChange}
                    disabled={!!formData.name}
                    required
                  />
                </div>
              </div>

              <div className="input-box">
                <label htmlFor="phone">What’s your Mobile No.?</label>
                <div className="input-flex">
                  <input
                    type="text"
                    name="phone"
                    placeholder="9876543210"
                    value={formData.phone}
                    onChange={handleChange}
                    disabled={!!formData.phone}
                    required
                  />
                </div>
              </div>

              <div className="input-box">
                <label htmlFor="email">What’s your email?</label>
                <div className="input-flex">
                  <input
                    type="email"
                    name="email"
                    placeholder="john.doe@mail.com"
                    value={formData.email}
                    onChange={handleChange}
                    disabled={!!formData.email}
                    required
                  />
                </div>
              </div>

              <div className="input-box">
                <label>Flight</label>
                <div className="input-flex">
                  <input
                    type="text"
                    value={
                      flightLoading
                        ? "Loading..."
                        : flightData?.flightType?.name || "Unknown Flight"
                    }
                    disabled
                  />
                </div>
              </div>

              <div className="input-flex-half">
                <div className="input-box">
                  <label htmlFor="date">Travel Date</label>
                  <div className="input-flex">
                    <input
                      type="date"
                      name="travelDate"
                      value={formData.travelDate}
                      onChange={handleChange}
                      disabled={!!formData.travelDate}
                      required
                    />
                  </div>
                </div>

                <div className="input-box">
                  <label htmlFor="time">Travel Time</label>
                  <div className="input-flex">
                    <input
                      type="time"
                      name="travelTime"
                      value={formData.travelTime}
                      onChange={handleChange}
                      disabled={!!formData.travelTime}
                      required
                    />
                  </div>
                </div>
              </div>

              <div className="input-box">
                <label htmlFor="remark">Remark</label>
                <div className="input-flex">
                  <textarea
                    name="remarks"
                    placeholder="Any special request..."
                    value={formData.remarks}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="form-button">
                <input
                  type="submit"
                  disabled={isLoading}
                  value={isLoading ? "Submitting..." : "Confirm Booking"}
                />
                <input
                  type="submit"
                  disabled={isLoading}
                  value={isLoading ? "Submitting..." : "Modify Search"}
                />
              </div>

              {isSuccess && <p style={{ color: "green" }}>Enquiry saved ✅</p>}
              {isError && (
                <p style={{ color: "red" }}>
                  Error: {error?.data?.message || "Something went wrong"}
                </p>
              )}
            </form>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}
