import { useSelector } from "react-redux";

export default function Results() {
  const { results, form } = useSelector((state) => state.search);

  if (!form) {
    return <p>No search performed yet.</p>;
  }

  return (
    <div className="results-page">
      <h2>
        Flights from {form.from} to {form.to} on {form.date}
      </h2>

      {results.length === 0 ? (
        <p>No flights found.</p>
      ) : (
        <ul className="flight-list">
          {results.map((flight) => (
            <li key={flight.id} className="flight-card">
              <h3>{flight.name}</h3>
              <p>
                Departure: {flight.departure} | Arrival: {flight.arrival}
              </p>
              <p>Price: â‚¹{flight.price}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
