import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useCreateEnquiryMutation } from "../../redux/api/bookingApi";
import Header from "../Header";
import Footer from "../Footer";
import { useSelector } from "react-redux";
import { useGetFlightTypeByIdQuery } from "../../redux/api/flightTypeApi";
import { useGetAirportByIdQuery } from "../../redux/api/airportApi";
import { airports } from "airports-json";

export default function EnquiryForm() {
  const { userInfo } = useSelector((state) => state.auth);
  const navigate = useNavigate();
  const location = useLocation();

  const query = new URLSearchParams(location.search);
  const flightId = query.get("flightId");
  const from = query.get("from");
  const to = query.get("to");
  const pax = query.get("pax");
  const travelDate = query.get("date");
  const travelTime = query.get("time");
  const tripType = query.get("tripType");
  const returnDate = query.get("returnDate");
  const returnTime = query.get("returnTime");

  const amount = location.state?.amount || 0;

  const { data: flightData, isLoading: flightLoading } =
    useGetFlightTypeByIdQuery(flightId);
  console.log("Flight Data:", flightData);
  // console.log("Airports data:", airports);
  // ✅ Fetch airport names by ID
  // ✅ Find airport details using IATA code
  const fromAirport = airports.find(
    (airport) => airport.iata_code?.toUpperCase() === from?.toUpperCase()
  );
  const toAirport = airports.find(
    (airport) => airport.iata_code?.toUpperCase() === to?.toUpperCase()
  );

  // Initialize state only once
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    fromPlace: "",
    toPlace: "",
    travelDate: "",
    travelTime: "",
    returnDate: "",
    returnTime: "",
    remarks: "",
    pax: 1,
    amount: parseFloat(amount).toFixed(2) || "0.00",
  });

  // ✅ Prefill only when component first loads
  useEffect(() => {
    const fromAirportName = fromAirport
      ? `${fromAirport.name} (${fromAirport.iata_code})`
      : from || "";

    const toAirportName = toAirport
      ? `${toAirport.name} (${toAirport.iata_code})`
      : to || "";

    setFormData((prev) => ({
      ...prev,
      name: userInfo?.user?.name || "",
      email: userInfo?.user?.email || "",
      phone: userInfo?.user?.phone || "",
      fromPlace: fromAirportName,
      toPlace: toAirportName,
      fromIataCode: from,
      toIataCode: to,
      pax: pax || 1,
      travelDate: travelDate || "",
      travelTime: travelTime || "",
      returnDate: returnDate || "",
      returnTime: returnTime || "",
      amount: parseFloat(amount).toFixed(2) || "0.00",
    }));

    // ✅ only run once — not on every re-render
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const [createEnquiry, { isLoading, error, isSuccess, isError }] =
    useCreateEnquiryMutation();

  const [showLoader, setShowLoader] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    setShowLoader(true); // show loader before API call
    try {
      await createEnquiry({
        ...formData,
        flightType: flightId,
        flightTypeName: flightData?.flightType?.name || "Unknown Flight",
        totalAmount: formData.amount,
        tripType,
      }).unwrap();
      // alert("Enquiry submitted successfully!");
      navigate("/thank-you");
    } catch (err) {
      console.error(err);
      alert("Failed to submit enquiry");
    } finally {
      setShowLoader(false); // hide loader after completion
    }
  };

  return (
    <>
      <Header />
      {/* ✅ Full-screen Loader Overlay */}
      {showLoader && (
        <div className="loader-overlay">
          <div className="loader"></div>
          <p>Please wait, sending booking enquiry...</p>
        </div>
      )}
      <div className="login-sec-container enquiry-sec-container">
        <div className="login-sec-form">
          <div className="form-flex mt-30">
            <form className="form" onSubmit={handleSubmit}>
              <div className="heading mb-30">Review Booking</div>

              <div className="input-box">
                <label htmlFor="name">What’s your good name?</label>
                <div className="input-flex">
                  <input
                    type="text"
                    name="name"
                    placeholder="John Doe"
                    value={formData.name}
                    onChange={handleChange}
                    disabled={!!formData.name}
                    required
                  />
                </div>
              </div>

              <div className="input-box">
                <label htmlFor="phone">What’s your Mobile No.?</label>
                <div className="input-flex">
                  <input
                    type="text"
                    name="phone"
                    placeholder="9876543210"
                    value={formData.phone}
                    onChange={handleChange}
                    disabled={!!formData.phone}
                    required
                  />
                </div>
              </div>

              <div className="input-box">
                <label htmlFor="email">What’s your email?</label>
                <div className="input-flex">
                  <input
                    type="email"
                    name="email"
                    placeholder="john.doe@mail.com"
                    value={formData.email}
                    onChange={handleChange}
                    disabled={!!formData.email}
                    required
                  />
                </div>
              </div>

              {/* ✅ From & To Fields */}
              <div className="input-box">
                <label htmlFor="fromPlace">From</label>
                <div className="input-flex">
                  <input
                    type="text"
                    name="fromPlace"
                    value={formData.fromPlace}
                    disabled
                  />
                </div>
              </div>

              <div className="input-box">
                <label htmlFor="toPlace">To</label>
                <div className="input-flex">
                  <input
                    type="text"
                    name="toPlace"
                    value={formData.toPlace}
                    disabled
                  />
                </div>
              </div>

              <div className="input-box">
                <label htmlFor="pax">No. of Passengers</label>
                <div className="input-flex">
                  <input
                    type="number"
                    name="pax"
                    min="1"
                    value={formData.pax}
                    onChange={handleChange}
                    disabled
                  />
                </div>
              </div>

              <div className="input-box">
                <label>Estimated Amount</label>
                <div className="input-flex">
                  <input
                    type="text"
                    name="amount"
                    value={formData.amount}
                    onChange={handleChange}
                    disabled
                  />
                </div>
              </div>

              <div className="input-box">
                <label>Flight</label>
                <div className="input-flex">
                  <input
                    type="text"
                    value={
                      flightLoading
                        ? "Loading..."
                        : flightData?.flightType?.name || "Unknown Flight"
                    }
                    disabled
                    // name="flightTypeName"
                    // onChange={handleChange}
                    // disabled={!!formData.flightTypeName}
                  />
                </div>
              </div>

              <div className="input-flex-half">
                <div className="input-box">
                  <label htmlFor="date">Travel Date</label>
                  <div className="input-flex">
                    <input
                      type="date"
                      name="travelDate"
                      value={formData.travelDate}
                      onChange={handleChange}
                      disabled={!!formData.travelDate}
                      required
                    />
                  </div>
                </div>

                <div className="input-box">
                  <label htmlFor="time">Travel Time</label>
                  <div className="input-flex">
                    <input
                      type="time"
                      name="travelTime"
                      value={formData.travelTime}
                      onChange={handleChange}
                      disabled={!!formData.travelTime}
                      required
                    />
                  </div>
                </div>
              </div>

              {tripType === "round" && (
                <div className="input-flex-half">
                  <div className="input-box">
                    <label htmlFor="returnDate">Return Date</label>
                    <div className="input-flex">
                      <input
                        type="date"
                        name="returnDate"
                        value={formData.returnDate}
                        onChange={handleChange}
                        disabled={!!formData.returnDate}
                        required
                      />
                    </div>
                  </div>

                  <div className="input-box">
                    <label htmlFor="returnTime">Return Time</label>
                    <div className="input-flex">
                      <input
                        type="time"
                        name="returnTime"
                        value={formData.returnTime}
                        onChange={handleChange}
                        disabled={!!formData.returnTime}
                        required
                      />
                    </div>
                  </div>
                </div>
              )}

              <div className="input-box">
                <label htmlFor="remark">Remark</label>
                <div className="input-flex">
                  <textarea
                    name="remarks"
                    placeholder="Any special request..."
                    value={formData.remarks}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="form-button">
                <input
                  type="submit"
                  disabled={isLoading}
                  value={isLoading ? "Submitting..." : "Confirm Booking"}
                />
                <input
                  type="button"
                  disabled={isLoading}
                  onClick={() => navigate("/")}
                  value="Modify Search"
                />
                {/* <button
                  type="button"
                  onClick={() => navigate("/")}
                  disabled={isLoading}
                >
                  Modify Search
                </button> */}
              </div>

              {isSuccess && <p style={{ color: "green" }}>Enquiry saved ✅</p>}
              {isError && (
                <p style={{ color: "red" }}>
                  Error: {error?.data?.message || "Something went wrong"}
                </p>
              )}
            </form>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}
