import React from "react";
import {
  useGetBookingByIdQuery,
  useGetBookingsQuery,
} from "../../redux/api/bookingApi";
import { Link } from "react-router-dom";

const BookingList = () => {
  const { data: bookings, isLoading } = useGetBookingsQuery();

  if (isLoading) return <p>Loading...</p>;

  return (
    <div className="container mt-3">
      <h2>All Bookings</h2>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Mobile</th>
            <th>From–To</th>
            <th>Date/Time</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {bookings?.map((b) => (
            <tr key={b._id}>
              <td>{b._id.slice(-5)}</td>
              <td>{b.travelerName}</td>
              <td>{b.email}</td>
              <td>{b.mobile}</td>
              <td>
                {b.from} → {b.to}
              </td>
              <td>
                {b.travelDate} {b.travelTime}
              </td>
              <td>{b.status}</td>
              <td>
                <Link to={`/admin/bookings/${b._id}`}>View</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BookingList;
