import { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useCreateEnquiryMutation } from "../../redux/api/bookingApi";

export default function EnquiryForm() {
  const navigate = useNavigate();
  const location = useLocation();
  //const flightId = new URLSearchParams(location.search).get("flightId");
  const query = new URLSearchParams(location.search);
  //console.log("Flight Detail Query Params:", Array.from(query.entries()));
  const flightId = query.get("flightId");
  const from = query.get("from");
  const to = query.get("to");
  const pax = query.get("pax");
  const travelDate = query.get("date");
  const travelTime = query.get("time");
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    fromPlace: from || "",
    toPlace: to || "",
    travelDate: travelDate || "",
    travelTime: travelTime || "",
    remarks: "",
  });

  const [createEnquiry, { isLoading }] = useCreateEnquiryMutation();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  console.log("Enquiry Form Flight ID:", flightId);
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await createEnquiry({
        ...formData,
        flightType: flightId,
        totalAmount: 0,
      }).unwrap();
      alert("Enquiry submitted successfully!");
      navigate("/");
    } catch (err) {
      console.error(err);
      alert("Failed to submit enquiry");
    }
  };

  return (
    <div className="container">
      <h2>Enquiry Form</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="fromPlace"
          placeholder="From"
          value={formData.fromPlace}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="toPlace"
          placeholder="To"
          value={formData.toPlace}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="travelDate"
          value={formData.travelDate}
          onChange={handleChange}
          required
        />
        <input
          type="time"
          name="travelTime"
          value={formData.travelTime}
          onChange={handleChange}
          required
        />
        <textarea
          name="remarks"
          placeholder="Any remarks"
          value={formData.remarks}
          onChange={handleChange}
        ></textarea>

        <button type="submit" disabled={isLoading}>
          {isLoading ? "Submitting..." : "Submit Enquiry"}
        </button>
      </form>
    </div>
  );
}
