import React, { useState } from "react";
import DataTable from "react-data-table-component";
import {
  useGetAirportsQuery,
  useDeleteAirportMutation,
} from "../../redux/api/airportApi";
import AirportForm from "./AirportForm";
import toast from "react-hot-toast";
import { MoreVertical, Edit, Trash } from "lucide-react";
import { Dropdown } from "react-bootstrap";

const AirportList = () => {
  const { data, isLoading, refetch } = useGetAirportsQuery();
  const [deleteAirport] = useDeleteAirportMutation();
  const [editingAirport, setEditingAirport] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [filterText, setFilterText] = useState("");

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure?")) {
      await deleteAirport(id);
      toast.success("Airport deleted");
      refetch();
    }
  };

  const filteredAirports = data?.airports?.filter((airport) =>
    Object.values(airport)
      .join(" ")
      .toLowerCase()
      .includes(filterText.toLowerCase())
  );

  const columns = [
    {
      name: "Full Name",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Code",
      selector: (row) => row.code,
      sortable: true,
    },
    {
      name: "Short Name",
      selector: (row) => row.shortName,
      sortable: true,
    },
    {
      name: "City",
      selector: (row) => row.city,
      sortable: true,
    },
    {
      name: "Country",
      selector: (row) => row.country,
      sortable: true,
    },
    {
      name: "Status",
      selector: (row) => row.status,
      sortable: true,
    },
    {
      name: "Actions",
      cell: (row) => (
        <Dropdown>
          <Dropdown.Toggle
            variant="light"
            size="sm"
            className="px-2 py-0 border"
            id={`dropdown-${row._id}`}
          >
            <MoreVertical size={16} />
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item
              onClick={() => {
                setEditingAirport(row);
                setShowForm(true);
              }}
            >
              <Edit size={14} className="me-2" />
              Edit
            </Dropdown.Item>
            <Dropdown.Item onClick={() => handleDelete(row._id)}>
              <Trash size={14} className="me-2 text-danger" />
              Delete
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  return (
    <div className="container mt-4">
      <h4 className="mb-3">Airport Management</h4>

      <div className="d-flex justify-content-between align-items-center mb-2">
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditingAirport(null);
            setShowForm(true);
          }}
        >
          Add Airport
        </button>

        <input
          type="text"
          placeholder="Search..."
          className="form-control w-25"
          value={filterText}
          onChange={(e) => setFilterText(e.target.value)}
        />
      </div>

      {showForm && (
        <AirportForm
          editingAirport={editingAirport}
          setShowForm={setShowForm}
          refetch={refetch}
        />
      )}

      <DataTable
        columns={columns}
        data={filteredAirports || []}
        progressPending={isLoading}
        pagination
        highlightOnHover
        responsive
        persistTableHead
        noDataComponent="No airports found"
      />
    </div>
  );
};

export default AirportList;
