import React, { useState } from "react";
import { useGetBookingsQuery } from "../../redux/api/bookingApi";
import { Link } from "react-router-dom";
import AddBookingForm from "./AddBookingForm";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

const BookingList = () => {
  const { data: bookings, isLoading, refetch } = useGetBookingsQuery();
  const [showForm, setShowForm] = useState(false);

  if (isLoading) return <p>Loading...</p>;

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>All Bookings</h2>
        <Button variant="primary" onClick={() => setShowForm(true)}>
          Add Booking
        </Button>
      </div>

      <table className="table table-bordered">
        <thead className="table-light">
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Mobile</th>
            <th>From–To</th>
            <th>Date/Time</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {bookings?.map((b) => (
            <tr key={b._id}>
              <td>{b._id.slice(-5)}</td>
              <td>{b.travelerName}</td>
              <td>{b.email}</td>
              <td>{b.mobile}</td>
              <td>
                {b.from} → {b.to}
              </td>
              <td>
                {b.travelDate} {b.travelTime}
              </td>
              <td>{b.status}</td>
              <td>
                <Link
                  to={`/admin/bookings/${b._id}`}
                  className="btn btn-sm btn-link"
                >
                  View
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal show={showForm} onHide={() => setShowForm(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Add New Booking</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <AddBookingForm
            onClose={() => setShowForm(false)}
            refetch={refetch}
          />
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default BookingList;
