import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

export default function FlightDetail() {
  const { id } = useParams(); // flight id from URL
  const [flight, setFlight] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFlight = async () => {
      try {
        const { data } = await axios.get(
          `${process.env.REACT_APP_API_URL}/flights/${id}`
        );
        setFlight(data.flight);
      } catch (error) {
        console.error("Error fetching flight:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFlight();
  }, [id]);

  if (loading) return <div className="loader">Loading...</div>;
  if (!flight) return <div className="error">Flight not found</div>;

  return (
    <>
      {/* Header */}
      <div className="inner-heading">
        <h1>{flight.name}</h1>
      </div>

      {/* Flight details */}
      <div className="container-fluid">
        <div className="flight-detail">
          <div className="thumb">
            <img
              src={
                flight.images?.[0]?.url ||
                `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
              }
              alt={flight.name}
              style={{ maxWidth: "100%", borderRadius: "8px" }}
            />
          </div>

          <div className="details">
            <h2>{flight.name}</h2>
            <p>{flight.description || "No description available"}</p>

            <ul className="stats">
              <li>
                <strong>Max Speed:</strong> {flight.maxSpeed} km/hr
              </li>
              <li>
                <strong>Capacity:</strong> {flight.capacity} Persons
              </li>
              <li>
                <strong>Baggage:</strong> {flight.baggage || "N/A"} kg
              </li>
              <li>
                <strong>Base Location:</strong> {flight.baseLocation?.name}
              </li>
              <li>
                <strong>Price:</strong> â‚¹ {flight.price?.toLocaleString()}
              </li>
              <li>
                <strong>Estimated Travel Time:</strong>{" "}
                {flight.travelTime || "N/A"} Minutes
              </li>
            </ul>

            <div className="actions">
              <Link to="/" className="btn btn-blank">
                Back
              </Link>
              <button className="btn btn-fill">Proceed to Booking</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
