import { useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useUpdateProfileMutation } from "../../redux/api/userApi"; // make sure you have this API endpoint
import { toast } from "react-toastify";

export default function UserProfile() {
  const navigate = useNavigate();
  const { userInfo } = useSelector((state) => state.auth);
  console.log("User Info in Profile:", userInfo);
  const [updateUser, { isLoading }] = useUpdateProfileMutation();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    avatar: null,
    avatarPreview: "",
  });

  useEffect(() => {
    if (userInfo) {
      setFormData({
        name: userInfo.user.name || "",
        email: userInfo.user.email || "",
        phone: userInfo.user.phone || "",
        avatar: null,
        avatarPreview: userInfo.user.avatar?.url || "",
      });
    }
  }, [userInfo]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "avatar" && files.length > 0) {
      const file = files[0];
      setFormData({
        ...formData,
        avatar: file,
        avatarPreview: URL.createObjectURL(file),
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await updateUser(formData).unwrap();
      alert("Profile updated successfully!");
      // ✅ Update Redux store
      dispatch(loginSuccess({ ...user, user: res.user }));
    } catch (err) {
      alert(err?.data?.message || "Failed to update profile");
    }

    // try {
    //   const data = new FormData();
    //   data.append("name", formData.name);
    //   data.append("phone", formData.phone);
    //   if (formData.avatar) data.append("avatar", formData.avatar);

    //   await updateUser(data).unwrap();
    //   toast.success("Profile updated successfully!");
    // } catch (err) {
    //   toast.error(err?.data?.message || "Failed to update profile");
    // }
  };

  return (
    <div className="login-sec-container">
      <div className="login-sec-info">
        <h1>Your Profile</h1>
        <p>Manage your personal information and preferences.</p>
      </div>

      <div className="login-sec-form">
        <div className="vector-aricraft">
          <img src="/assets/images/aircraft_with_trail.png" alt="Aircraft" />
        </div>
        <div className="logo">
          <a href="/">
            <img src="/assets/images/aayalogo-big.png" alt="Aayo" />
          </a>
        </div>

        <div className="form-flex">
          <div className="form">
            <div className="heading">Profile Details</div>
            <div className="subheading">
              Keep your account details up to date.
            </div>

            <form onSubmit={handleSubmit}>
              {/* Avatar Upload */}
              <div className="profile-avatar text-center mb-3">
                <label htmlFor="avatarUpload">
                  <img
                    src={
                      formData.avatarPreview || "/assets/images/thumb-dummy.jpg"
                    }
                    alt="Avatar"
                    style={{
                      width: 100,
                      height: 100,
                      borderRadius: "50%",
                      cursor: "pointer",
                      objectFit: "cover",
                    }}
                  />
                </label>
                <input
                  type="file"
                  id="avatarUpload"
                  name="avatar"
                  accept="image/*"
                  onChange={handleChange}
                  style={{ display: "none" }}
                />
                <p style={{ fontSize: "0.9rem", color: "#777" }}>
                  Click image to change
                </p>
              </div>

              {/* Name */}
              <div className="input-box">
                <label>Full Name</label>
                <div className="input-flex">
                  <div className="icon">
                    <i className="fa fa-user"></i>
                  </div>
                  <input
                    type="text"
                    name="name"
                    placeholder="John Doe"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              {/* Email (read-only) */}
              <div className="input-box">
                <label>Email Id</label>
                <div className="input-flex">
                  <div className="icon">
                    <img src="/assets/images/icon-email.png" alt="email" />
                  </div>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    readOnly
                    style={{ background: "#f5f5f5" }}
                  />
                </div>
              </div>

              {/* Phone */}
              <div className="input-box">
                <label>Phone</label>
                <div className="input-flex">
                  <div className="icon">
                    <i className="fa fa-phone"></i>
                  </div>
                  <input
                    type="text"
                    name="phone"
                    placeholder="9876543210"
                    value={formData.phone}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="form-button">
                <input
                  type="submit"
                  className="btn btn-fill"
                  value={isLoading ? "Updating..." : "Update Profile"}
                />
              </div>

              <div className="form-bottom-link">
                <p>
                  Want to change your password?{" "}
                  <span
                    style={{ cursor: "pointer", color: "#007bff" }}
                    onClick={() => navigate("/change-password")}
                  >
                    Click here
                  </span>
                </p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
