import React from "react";
import { useNavigate } from "react-router-dom";
import {
  useGetFlightCategoriesQuery,
  useDeleteFlightCategoryMutation,
} from "../../redux/api/flightCategoryApi";
import DataTable from "react-data-table-component";
import toast from "react-hot-toast";

const FlightCategoryList = () => {
  const navigate = useNavigate();
  const { data, isLoading, refetch } = useGetFlightCategoriesQuery();
  const [deleteCategory] = useDeleteFlightCategoryMutation();

  const handleEdit = (id) => {
    navigate(`/admin/flight-categories/edit/${id}`);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this category?")) {
      try {
        await deleteCategory(id).unwrap();
        toast.success("Flight Category deleted successfully");
        refetch();
      } catch (err) {
        toast.error(err?.data?.message || "Something went wrong");
      }
    }
  };

  const columns = [
    {
      name: "Image",
      selector: (row) => (
        <img
          src={row.image?.url}
          alt={row.name}
          width="50"
          height="50"
          style={{ borderRadius: "5px" }}
        />
      ),
    },
    {
      name: "Name",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "Description",
      selector: (row) => row.description,
    },
    {
      name: "Actions",
      cell: (row) => (
        <div style={{ display: "flex", gap: "10px" }}>
          <button
            className="btn btn-sm btn-primary"
            onClick={() => handleEdit(row._id)}
          >
            Edit
          </button>
          <button
            className="btn btn-sm btn-danger"
            onClick={() => handleDelete(row._id)}
          >
            Delete
          </button>
        </div>
      ),
    },
  ];

  return (
    <div>
      <div className="d-flex justify-content-between mb-3">
        <h4>Flight Categories</h4>
        <button
          className="btn btn-success"
          onClick={() => navigate("/admin/flight-categories/create")}
        >
          Add Category
        </button>
      </div>

      <DataTable
        columns={columns}
        data={data?.flightCategories || []}
        progressPending={isLoading}
        pagination
      />
    </div>
  );
};

export default FlightCategoryList;
