import { useParams, useLocation, useNavigate } from "react-router-dom";
import { useGetFlightByIdQuery } from "../../redux/api/flightApi";
import Header from "../Header";
import Footer from "../Footer";
import { useState, useEffect } from "react";

export default function FlightDetail() {
  const [amount, setAmount] = useState(0);

  const navigate = useNavigate();
  const { id } = useParams();
  const location = useLocation();
  const query = new URLSearchParams(location.search);

  const from = query.get("from");
  const to = query.get("to");
  const pax = query.get("pax");
  const travelDate = query.get("date");
  const travelTime = query.get("time");
  const tripType = query.get("trip") || "oneway"; // ‚úÖ one-way or return
  const returnDate = query.get("returnDate") || null;

  const { data, error, isLoading } = useGetFlightByIdQuery({
    id,
    from,
    to,
    pax,
  });

  // ‚úÖ Calculate price properly
  useEffect(() => {
    if (!data?.flight?.priceHeads?.length || !data?.flight?.travelTime) return;

    const flight = data.flight;
    const travelHours = (Number(flight.travelTime) || 0) / 60;

    // ‚úÖ Extract Flying Cost head
    const flyingCostHead = flight.priceHeads.find(
      (h) => h.head?.headName?.toLowerCase() === "flying cost"
    );
    const flyingCostPerHour = Number(flyingCostHead?.amount) || 0;
    const flyingCostTotal = flyingCostPerHour * travelHours;

    // ‚úÖ Other price heads (exclude flying)
    const otherHeads = flight.priceHeads.filter(
      (h) => h.head?.headName?.toLowerCase() !== "flying cost"
    );
    let otherCostTotal = otherHeads.reduce(
      (sum, h) => sum + (Number(h.amount) || 0),
      0
    );

    // ‚úÖ RETURN TRIP EXTRA DAY CALCULATION
    let crewAccommodationAmount = 0;

    if (tripType === "return" && returnDate) {
      const start = new Date(travelDate);
      const end = new Date(returnDate);

      let diffDays = Math.ceil((end - start) / (1000 * 60 * 60 * 24));

      // If same day return = no charge
      if (diffDays > 0) {
        const crewHead = flight.priceHeads.find(
          (h) => h.head?.headName?.toLowerCase() === "crew accommodation"
        );

        if (crewHead) {
          crewAccommodationAmount = Number(crewHead.amount) * diffDays;
          otherCostTotal += crewAccommodationAmount;
        }
      }
    }

    // ‚úÖ Total Calculation
    const basePrice = flyingCostTotal + otherCostTotal;
    const taxesAndFees = Math.round(basePrice * 0.12);
    const finalTotal = Math.round(basePrice + taxesAndFees);

    setAmount(finalTotal);
  }, [data, tripType, returnDate, travelDate]);

  if (isLoading) return <div className="loader">Loading...</div>;
  if (error) return <div className="error">Error loading flight details</div>;

  const flight = data?.flight;
  const fromAirport = data?.fromAirport;
  const toAirport = data?.toAirport;
  if (!flight) return <div className="error">Flight not found</div>;

  const handleEnquiryClick = () => {
    const params = `?flightId=${id}&from=${from}&to=${to}&pax=${pax}&date=${travelDate}&time=${travelTime}&trip=${tripType}&returnDate=${returnDate}`;
    const isLoggedIn = localStorage.getItem("token");

    if (isLoggedIn) {
      navigate(`/enquiry${params}`, { state: { amount } });
    } else {
      navigate(`/login${params}`, { state: { amount } });
    }
  };

  return (
    <>
      <Header />

      <div className="inner-heading">
        <h1>Flight Details</h1>
      </div>

      <div className="summary-container mt-30">
        <div className="container-fluid">
          <div className="summary-box-container">
            <div className="summary">
              {/* ===== LEFT SIDE ===== */}
              <div className="summary-content">
                <div className="summary-banner">
                  <img
                    src={
                      flight.images?.[0]?.url ||
                      `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
                    }
                    alt={flight.name}
                  />
                </div>

                <div className="summary-header">
                  <div className="summary-headerleft">
                    <h2>{flight.name}</h2>
                    <ul className="details">
                      <li>üìÖ {travelDate}</li>
                      <li>‚è∞ {travelTime}</li>
                      <li>üë• {pax} Passengers</li>
                      <li>
                        ‚úàÔ∏è Trip: {tripType === "return" ? "Return" : "One-Way"}
                      </li>
                      {tripType === "return" && returnDate && (
                        <li>‚Ü© Return Date: {returnDate}</li>
                      )}
                    </ul>
                  </div>
                </div>
              </div>

              {/* ===== RIGHT SIDE ‚Äî BILLING ===== */}
              <div className="summary-details">
                <h2>Price Breakdown</h2>

                <div className="summary-detail-box">
                  {(() => {
                    const flight = data.flight;
                    const travelHours = (Number(flight.travelTime) || 0) / 60;

                    const flyingHead = flight.priceHeads.find(
                      (h) => h.head?.headName?.toLowerCase() === "flying cost"
                    );
                    const flyingPerHr = Number(flyingHead?.amount) || 0;
                    const flyingTotal = flyingPerHr * travelHours;

                    const otherHeads = flight.priceHeads.filter(
                      (h) => h.head?.headName?.toLowerCase() !== "flying cost"
                    );

                    let otherTotal = otherHeads.reduce(
                      (sum, h) => sum + (Number(h.amount) || 0),
                      0
                    );

                    let crewDays = 0;
                    if (tripType === "return" && returnDate) {
                      let d1 = new Date(travelDate);
                      let d2 = new Date(returnDate);
                      crewDays = Math.ceil((d2 - d1) / 86400000);
                    }

                    const base = flyingTotal + otherTotal;
                    const gst = Math.round(base * 0.12);
                    const final = base + gst;

                    return (
                      <>
                        <div className="summary-detail">
                          <div className="heading">Flying Cost</div>
                          <div className="text">
                            ‚Çπ {flyingTotal.toLocaleString()}
                          </div>
                        </div>

                        {otherHeads.map((head, i) => (
                          <div className="summary-detail" key={i}>
                            <div className="heading">{head.head?.headName}</div>
                            <div className="text">
                              ‚Çπ {Number(head.amount).toLocaleString()}
                            </div>
                          </div>
                        ))}

                        {tripType === "return" && crewDays > 0 && (
                          <div className="summary-detail">
                            <div className="heading">
                              Crew Stay ({crewDays} Days)
                            </div>
                            <div className="text">
                              ‚Çπ{" "}
                              {(
                                Number(
                                  flight.priceHeads.find(
                                    (h) =>
                                      h.head?.headName?.toLowerCase() ===
                                      "crew accommodation"
                                  )?.amount || 0
                                ) * crewDays
                              ).toLocaleString()}
                            </div>
                          </div>
                        )}

                        <div className="summary-detail">
                          <div className="heading">GST (12%)</div>
                          <div className="text">‚Çπ {gst.toLocaleString()}</div>
                        </div>

                        <hr />

                        <div className="summary-detail grand-total">
