import React, { useEffect, useState } from "react";
import {
  useDeleteBookingMutation,
  useGetBookingsQuery,
} from "../../redux/api/bookingApi";
import { Link, useSearchParams } from "react-router-dom";
import AddBookingForm from "./AddBookingForm";
import toast from "react-hot-toast";
import AddPaymentForm from "./AddPaymentForm";
import CustomPagination from "../layout/CustomPagination";

const BookingList = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const pageParam = Number(searchParams.get("page")) || 1;

  const [currentPage, setCurrentPage] = useState(pageParam);

  // Pass currentPage to RTK query
  const { data, isLoading, refetch } = useGetBookingsQuery(currentPage);
  const bookings = data?.bookings || [];
  const resPerPage = data?.resPerPage || 10;
  const totalResults = data?.filteredBookingsCount || 0;

  const [deleteBooking] = useDeleteBookingMutation();
  const [showForm, setShowForm] = useState(false);
  const [editBooking, setEditBooking] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null); // For payment
  const [showPayment, setShowPayment] = useState(false);

  useEffect(() => {
    setCurrentPage(pageParam);
  }, [pageParam]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    setSearchParams({ page: pageNumber }); // updates URL
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this booking?")) {
      try {
        await deleteBooking(id).unwrap();
        toast.success("Booking deleted");
        refetch();
      } catch (err) {
        toast.error("Failed to delete booking");
      }
    }
  };

  const handleEdit = (booking) => {
    setEditBooking(booking);
    setShowForm(true);
    setShowPayment(false);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditBooking(null);
  };

  const handlePaymentClick = (booking) => {
    setSelectedBooking(booking);
    setShowForm(false);
    setShowPayment(true);
    setTimeout(() => {
      const paymentForm = document.getElementById("payment-form");
      if (paymentForm) {
        paymentForm.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Bookings</h2>
        <button
          className="btn btn-primary"
          onClick={() => {
            setEditBooking(null);
            setShowForm(!showForm);
            setShowPayment(false);
          }}
        >
          {showForm ? "Close Form" : "Add Booking"}
        </button>
      </div>

      {showForm && (
        <AddBookingForm
          setShowForm={handleCloseForm}
          refetch={refetch}
          booking={editBooking}
        />
      )}

      {showPayment && selectedBooking && (
        <AddPaymentForm
          booking={selectedBooking}
          refetch={refetch}
          onClose={() => setShowPayment(false)}
        />
      )}

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
          <div className="table-responsive">
            <table className="table mt-4">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Traveler</th>
                  <th>Email</th>
                  <th>Mobile</th>
                  <th>From–To</th>
                  <th>Date/Time</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {bookings?.map((b) => (
                  <tr key={b._id}>
                    <td>{b._id.slice(-5)}</td>
                    <td>{b.user?.name}</td>
                    <td>{b.user?.email}</td>
                    <td>{b.user?.mobile}</td>
                    <td>
                      {b.fromPlace} → {b.toPlace}
                    </td>
                    <td>
                      {b.travelDate} {b.travelTime}
                    </td>
                    <td>
                      <span
                        className={`badge bg-${
                          b.status === "confirmed"
                            ? "success"
                            : b.status === "cancelled"
                            ? "danger"
                            : "warning"
                        }`}
                      >
                        {b.status}
                      </span>
                    </td>
                    <td>
                      <div className="dropdown">
                        <button
                          className="btn btn-sm btn-secondary dropdown-toggle"
                          type="button"
                          data-bs-toggle="dropdown"
                          aria-expanded="false"
                        >
                          Actions
                        </button>
                        <ul className="dropdown-menu">
                          <li>
                            <Link
                              to={`/admin/bookings/${b._id}`}
                              className="dropdown-item"
                            >
                              View
                            </Link>
                          </li>
                          <li>
                            <button
                              className="dropdown-item"
                              onClick={() => handleEdit(b)}
                            >
                              Edit
                            </button>
                          </li>
                          <li>
                            <button
                              className="dropdown-item"
                              onClick={() => handleDelete(b._id)}
                            >
                              Delete
                            </button>
                          </li>
                          <li>
                            <button
                              className="dropdown-item"
                              onClick={() => handlePaymentClick(b)}
                            >
                              Add Payment
                            </button>
                          </li>
                        </ul>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <CustomPagination
            resPerPage={resPerPage}
            totalResults={totalResults}
            currentPage={currentPage}
            onPageChange={handlePageChange}
          />
        </>
      )}
    </div>
  );
};

export default BookingList;
