import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { useState, useMemo } from "react";

export default function Results() {
  const { results = {}, form = {} } = useSelector(
    (state) => state.search || {}
  );
  const tripType = form?.tripType || "oneway"; // "oneway" | "round"

  const [priceFilters, setPriceFilters] = useState(["All"]);
  const [capacityFilters, setCapacityFilters] = useState(["All"]);

  // ------------------ Helpers ------------------
  const matchPriceRange = (price, range) => {
    switch (range) {
      case "₹ 0 - 1,00,000":
        return price >= 0 && price <= 100000;
      case "₹ 1,00,000 - 2,00,000":
        return price > 100000 && price <= 200000;
      case "₹ 2,00,000 - 3,00,000":
        return price > 200000 && price <= 300000;
      case "₹ 3,00,000+":
        return price > 300000;
      default:
        return true;
    }
  };

  const matchCapacity = (capacity, range) => {
    switch (range) {
      case "5 Persons":
        return capacity === 5;
      case "6 Persons":
        return capacity === 6;
      case "7 Persons":
        return capacity === 7;
      case "8 Persons":
        return capacity === 8;
      case "9+ Persons":
        return capacity >= 9;
      default:
        return true;
    }
  };

  const calculateTotalPrice = (flight) => {
    const flyingCostHead = flight.priceHeads?.find(
      (h) => h.head === "68a2c4d345a4c290c0959fe5"
    );
    const otherHeads =
      flight.priceHeads?.filter((h) => h.head !== "68a2c4d345a4c290c0959fe5") ||
      [];

    const travelHours = (Number(flight.travelTime + 30) || 0) / 60;
    const flyingCostTotal = (Number(flyingCostHead?.amount) || 0) * travelHours;
    const otherCostTotal = otherHeads.reduce(
      (sum, h) => sum + (Number(h.amount) || 0),
      0
    );

    const basePrice = flyingCostTotal + otherCostTotal;
    const taxes = Math.round(basePrice * 0.12);
    return basePrice + taxes; // or just basePrice if you exclude taxes
  };

  // ------------------ Filtering ------------------
  const filterFlights = (flights) => {
    let list = flights || [];

    if (!priceFilters.includes("All")) {
      list = list.filter((f) => {
        const price = calculateTotalPrice(f);
        return priceFilters.some((range) => matchPriceRange(price, range));
      });
    }

    if (!capacityFilters.includes("All")) {
      list = list.filter((f) =>
        capacityFilters.some((range) =>
          matchCapacity(Number(f.capacity || 0), range)
        )
      );
    }

    return list;
  };

  const onwardFlights = useMemo(
    () => filterFlights(results?.onwardFlights || results?.flights || []),
    [results, priceFilters, capacityFilters]
  );
  const returnFlights = useMemo(
    () => filterFlights(results?.returnFlights || []),
    [results, priceFilters, capacityFilters]
  );

  // ------------------ UI ------------------
  const renderFlightList = (flights, title) => (
    <div className="listing-box">
      <h2 className="flight-section-title">{title}</h2>
      {flights?.length ? (
        flights.map((flight) => (
          <div key={flight._id} className="listing-row">
            <div className="thumb">
              <img
                src={
                  flight.images?.[0]?.url ||
                  `${process.env.PUBLIC_URL}/assets/images/default-flight.jpg`
                }
                alt={flight.name}
                height={200}
                width={200}
              />
            </div>
            <div className="content">
              <h2>{flight.name}</h2>
              {(flight.description &&
                flight.description.replace(/<[^>]+>/g, "")) ||
                "No description available"}

              <div className="stats">
                <div className="stats-col">
                  <div className="icon">
                    <img
                      src={`${process.env.PUBLIC_URL}/assets/images/icon-speed.png`}
                      alt="speed"
                    />
                  </div>
                  <p>{flight.maxSpeed || "N/A"} km/hr</p>
                </div>
                <div className="stats-col">
                  <div className="icon">
                    <img
                      src={`${process.env.PUBLIC_URL}/assets/images/icon-person.png`}
                      alt="capacity"
                    />
                  </div>
                  <p>{flight.capacity} Person</p>
                </div>
                <div className="stats-col">
                  <div className="icon">
                    <img
                      src={`${process.env.PUBLIC_URL}/assets/images/icon-time.png`}
                      alt="time"
                    />
                  </div>
                  <p>
                    {Math.floor((flight.travelTime + 30) / 60)} hr{" "}
                    {(flight.travelTime + 30) % 60} min
                  </p>
                </div>
                <div className="stats-col">
                  <div className="icon">
                    <img
                      src={`${process.env.PUBLIC_URL}/assets/images/icon-luggage.png`}
                      alt="luggage"
                    />
                  </div>
                  <p>Upto {flight.baggage || "N/A"} kg</p>
                </div>
              </div>
            </div>

            {/* Price */}
            <div className="price">
              <div className="label">Price</div>
              <div className="amount">
                ₹{" "}
                {calculateTotalPrice(flight).toLocaleString(undefined, {
                  minimumFractionDigits: 2,
                  maximumFractionDigits: 2,
                })}
              </div>
              <div className="button">
                <Link
                  to={`/flights/${flight._id}?from=${form.from}&to=${
                    form.to
                  }&pax=${form.pax}&date=${form.date}&time=${form.time}${
                    tripType === "round"
                      ? `&returnDate=${form.returnDate}&returnTime=${form.returnTime}`
                      : ""
                  }`}
                >
                  View Details
                </Link>
              </div>
            </div>
          </div>
        ))
      ) : (
        <div className="nomore-records">No matching flights found</div>
      )}
    </div>
  );

  // Handle checkbox filter changes
  const handleCheckboxChange = (e, type, value) => {
    if (type === "price") {
      if (value === "All") {
        setPriceFilters(["All"]);
      } else {
        setPriceFilters((prev) => {
          const updated = prev.includes(value)
            ? prev.filter((v) => v !== value)
            : [...prev.filter((v) => v !== "All"), value];

          return updated.length === 0 ? ["All"] : updated;
        });
      }
    }

    if (type === "capacity") {
      if (value === "All") {
        setCapacityFilters(["All"]);
      } else {
        setCapacityFilters((prev) => {
          const updated = prev.includes(value)
            ? prev.filter((v) => v !== value)
            : [...prev.filter((v) => v !== "All"), value];

          return updated.length === 0 ? ["All"] : updated;
        });
      }
    }
  };

  return (
    <>
      {/* Header */}
      <div className="header-container">
        <div className="container-fluid">
          <div className="header">
            <div className="logo">
              <Link to="/">
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/aayologo.png`}
                  alt="Aayo"
                />
              </Link>
            </div>
            <div className="nav">
              <ul>
                <li>
                  <Link className="active" to="/">
                    home
                  </Link>
                </li>
                <li>
                  <Link to="/features">features</Link>
                </li>
                <li>
                  <Link to="/about">about us</Link>
                </li>
                <li>
                  <Link to="/contact">contact us</Link>
                </li>
              </ul>
            </div>
            <div className="button">
              <Link to="/login" className="btn btn-blank">
                Sign in
              </Link>
              <Link to="/register" className="btn btn-fill">
                Sign up
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Heading */}
      <div className="inner-heading">
        <h1>
          {tripType === "round" ? "Round Trip Flights" : "Available Flights"}
        </h1>
      </div>

      {/* From / To summary */}
      <div className="match-notfound">
        <div className="location-box available-flight">
          <div className="both-location">
            <div className="both-location-left">
              <div className="both-location-heading">
                {results.fromAirport?.iata_code || "FROM"}
              </div>
              <div className="both-location-fullname">
                ({results.fromAirport?.code || "Origin"})
              </div>
            </div>
            <div className="both-location-middle">
              <div className="icon">
                <img
                  src={`${process.env.PUBLIC_URL}/assets/images/icon-airplane-color.png`}
                  alt="airplane"
                />
              </div>
            </div>
            <div className="both-location-right">
              <div className="both-location-heading">
                {results.toAirport?.iata_code || "TO"}
              </div>
              <div className="both-location-fullname">
                ({results.toAirport?.code || "Destination"})
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Flights Listing */}
      <div className="listing-container">
        <div className="container-fluid">
          <div className="listing">
            {/* Filters */}
            <div className="filter">
              <div className="filter-box">
                <h2>Price Range</h2>
                <ul>
                  {[
                    "All",
                    "₹ 0 - 1,00,000",
                    "₹ 1,00,000 - 2,00,000",
                    "₹ 2,00,000 - 3,00,000",
                    "₹ 3,00,000+",
                  ].map((range) => (
                    <li key={range}>
                      <label className="checkbox style-c">
                        <input
                          type="checkbox"
                          checked={priceFilters.includes(range)}
                          onChange={(e) =>
                            handleCheckboxChange(e, "price", range)
                          }
                        />
                        <div className="checkbox__checkmark"></div>
                      </label>{" "}
                      {range}
                    </li>
                  ))}
                </ul>
              </div>

              <div className="filter-box">
                <h2>Capacity</h2>
                <ul>
                  {[
                    "All",
                    "5 Persons",
                    "6 Persons",
                    "7 Persons",
                    "8 Persons",
                    "9+ Persons",
                  ].map((cap) => (
                    <li key={cap}>
                      <label className="checkbox style-c">
                        <input
                          type="checkbox"
                          checked={capacityFilters.includes(cap)}
                          onChange={(e) =>
                            handleCheckboxChange(e, "capacity", cap)
                          }
                        />
                        <div className="checkbox__checkmark"></div>
                      </label>{" "}
                      {cap}
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            {/* Flight Listings */}
            {tripType === "round" ? (
              <>
                {renderFlightList(onwardFlights, "Onward Flights")}
                {renderFlightList(returnFlights, "Return Flights")}
              </>
            ) : (
              renderFlightList(onwardFlights, "Available Flights")
            )}
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer-container">
        <div className="container-fluid">
          <div className="footer">
            {/* ... your existing footer content ... */}
          </div>
        </div>
      </footer>
    </>
  );
}
