import React, { useState } from "react";
import { useCreateFlightTypeMutation } from "../../redux/api/flightTypeApi";
import toast from "react-hot-toast";

const FlightTypeForm = () => {
  const [form, setForm] = useState({
    name: "",
    capacity: "",
    description: "",
    maxSpeed: "",
  });
  const [image, setImage] = useState(null);
  const [createFlightType] = useCreateFlightTypeMutation();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const fd = new FormData();
    Object.entries(form).forEach(([key, val]) => fd.append(key, val));
    if (image) fd.append("image", image);

    try {
      await createFlightType(fd).unwrap();
      toast.success("Flight Type created successfully");
      setForm({ name: "", capacity: "", description: "", maxSpeed: "" });
      setImage(null);
    } catch (err) {
      toast.error("Failed to create flight type");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 border rounded">
      <h4>Add Flight Type</h4>
      <input
        type="text"
        name="name"
        className="form-control my-2"
        placeholder="Name"
        value={form.name}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="capacity"
        className="form-control my-2"
        placeholder="Capacity"
        value={form.capacity}
        onChange={handleChange}
        required
      />
      <textarea
        name="description"
        className="form-control my-2"
        placeholder="Description"
        value={form.description}
        onChange={handleChange}
      ></textarea>
      <input
        type="number"
        name="maxSpeed"
        className="form-control my-2"
        placeholder="Max Speed"
        value={form.maxSpeed}
        onChange={handleChange}
      />
      <input
        type="file"
        className="form-control my-2"
        accept="image/*"
        onChange={(e) => setImage(e.target.files[0])}
      />
      <button type="submit" className="btn btn-primary mt-2">
        Create
      </button>
    </form>
  );
};

export default FlightTypeForm;
