import { set } from "mongoose";
import React, { useEffect, useState } from "react";
import Pagination from "react-js-pagination";
import { useSearchParams } from "react-router-dom";

const CustomPagination = ({ resPerPage, totalResults }) => {
  console.log("CustomPagination:", resPerPage, totalResults);
  const [currentPage, setCurrentPage] = useState();
  const [searchParams] = useSearchParams();
  const page = Number(searchParams.get("page")) || 1;
  const onPageChange = (pageNumber) => {
    window.history.pushState(null, "", `?page=${pageNumber}`);
  };

  useEffect(() => {
    setCurrentPage(page);
  }, [page]);

  const setCurrentPageNumber = (pageNumber) => {
    setCurrentPage(pageNumber);
    onPageChange(pageNumber);
  };
  return (
    <div>
      {totalResults > resPerPage && (
        <Pagination
          activePage={currentPage}
          itemsCountPerPage={resPerPage}
          totalItemsCount={totalResults}
          onChange={setCurrentPageNumber}
          nextPageText={<span aria-label="Next">Next</span>}
          prevPageText={<span aria-label="Previous">Previous</span>}
          firstPageText={<span aria-label="First">First</span>}
          lastPageText={<span aria-label="Last">Last</span>}
        />
      )}
    </div>
  );
};

export default CustomPagination;
