import {
  useSearchParams,
  useNavigate,
  useLocation,
  Link,
} from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import { useDispatch } from "react-redux";
import {
  useVerifyOtpMutation,
  useSendOtpMutation,
} from "../../redux/api/authApi";
import { loginSuccess } from "../../redux/slices/authSlice";

export default function OtpVerification() {
  const [searchParams] = useSearchParams();
  console.log("Search Params:", Object.fromEntries([...searchParams]));
  const navigate = useNavigate();
  const location = useLocation();
  const dispatch = useDispatch();

  const amount = location.state?.amount || 0;

  const email = searchParams.get("email");
  const phone = searchParams.get("phone");
  const isRegistration = searchParams.get("isRegistration") === "true"; // ✅ detect registration flow
  const bookingParams = location.search;

  const state = location.state || {};
  const name = state.name || "";
  const password = state.password || "";

  const [otpValues, setOtpValues] = useState(["", "", "", ""]);
  const [verifyOtp, { isLoading }] = useVerifyOtpMutation();
  const [sendOtp, { isLoading: resendLoading }] = useSendOtpMutation();
  const inputRefs = useRef([]);

  // Detect whether the user came from booking/enquiry
  const isFromBooking =
    bookingParams.includes("tripType") ||
    bookingParams.includes("date") ||
    bookingParams.includes("enquiry");

  // Move focus between OTP inputs
  const handleChange = (index, value) => {
    if (/^[0-9]?$/.test(value)) {
      const newOtp = [...otpValues];
      newOtp[index] = value;
      setOtpValues(newOtp);
      if (value && index < otpValues.length - 1) {
        inputRefs.current[index + 1].focus();
      }
    }
  };

  const handleKeyDown = (e, index) => {
    if (e.key === "Backspace" && !otpValues[index] && index > 0) {
      inputRefs.current[index - 1].focus();
    }
  };

  // ✅ Handle OTP Verification
  const handleVerify = async (e) => {
    e.preventDefault();
    const otp = otpValues.join("");
    if (otp.length < 4) {
      alert("Please enter the 4-digit OTP");
      return;
    }

    try {
      const res = await verifyOtp({
        email: email || undefined,
        phone: phone || undefined,
        name, // send name
        password, // send password
        otp,
        isRegistration, // ✅ pass registration flag to backend
      }).unwrap();

      console.log("OTP Verification Response:", res);
      dispatch(loginSuccess(res));
      alert(
        isRegistration
          ? "Registration completed successfully!"
          : "OTP verified successfully!"
      );

      // ✅ Smart Redirect
      if (isFromBooking) {
        navigate(`/enquiry${bookingParams}`, { state: { amount } });
      } else {
        navigate(`/`);
      }
    } catch (err) {
      alert(err?.data?.message || "Invalid OTP");
    }
  };

  // ✅ Resend OTP
  const handleResend = async () => {
    try {
      await sendOtp({ email, phone }).unwrap();
      alert("OTP resent successfully!");
      setOtpValues(["", "", "", ""]);
      inputRefs.current[0].focus();
    } catch (err) {
      alert(err?.data?.message || "Failed to resend OTP");
    }
  };

  useEffect(() => {
    inputRefs.current[0]?.focus();
  }, []);

  return (
    <div className="login-sec-container">
      {/* Left side info */}
      <div className="login-sec-info">
        <h1>
          Fly Beyond Limits <br /> with Aayo
        </h1>
        <p>
          Travel is the only purchase that enriches you in ways beyond material
          wealth.
        </p>
      </div>

      {/* Right side form */}
      <div className="login-sec-form">
        <div className="vector-aricraft">
          <img src="/assets/images/aircraft_with_trail.png" alt="Aircraft" />
        </div>

        <div className="logo">
          <a href="/">
            <img src="/assets/images/aayalogo-big.png" alt="Aayo" />
          </a>
        </div>

        <div className="form-flex">
          <div className="form">
            <div className="heading">Verification Code</div>
            <div className="subheading">
              {isRegistration
                ? "You have registered successfully! Please verify your email address using the OTP we’ve sent to your registered email ID to complete your login process."
                : "Please enter the OTP sent to your email or phone number to verify your account."}
              {/* A 4-digit code has been sent to <a href="#">{email || phone}</a> */}
            </div>

            <form onSubmit={handleVerify}>
              <div className="verify-inputs">
                {otpValues.map((num, index) => (
                  <input
                    key={index}
                    type="text"
                    className="num"
                    maxLength="1"
                    inputMode="numeric"
                    pattern="[0-9]*"
                    value={num}
                    onChange={(e) => handleChange(index, e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, index)}
                    ref={(el) => (inputRefs.current[index] = el)}
                    required
                  />
                ))}
              </div>

              <div className="form-link text-center">
                Didn’t receive code?{" "}
                <Link
                  to="#"
                  className="resend-link"
                  onClick={handleResend}
                  disabled={resendLoading}
                >
                  {resendLoading ? "Resending..." : "Resend code"}
                </Link>
              </div>

              <div className="form-button">
                <input
                  type="submit"
                  disabled={isLoading}
                  value={
                    isLoading
                      ? "Verifying..."
                      : isRegistration
                      ? "Complete Registration"
                      : "Verify"
                  }
                />
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
